    .playlist-li.active::before {
        content: '♪';
        margin-right: 5px;
        color: #1a237e;
    }

</style>
</head>
<body>

<div class="music-player-wrapper" id="nova-player-root">
    <!-- 原版卡片部分 (添加了ID以便JS控制) -->
    <div class="nova-aether-v2">
        <div class="aether-bg"></div>
        <div class="aether-stars">
            <div class="star-dot" style="top: 15%; left: 85%; width: 4px; height: 4px; animation-delay: 0.2s;"></div>
            <div class="star-dot" style="top: 65%; left: 8%; width: 3px; height: 3px; animation-delay: 1.5s;"></div>
            <div class="star-dot" style="top: 35%; left: 50%; width: 2px; height: 2px; animation-delay: 2.2s;"></div>
            <div class="star-dot" style="top: 80%; left: 92%; width: 3px; height: 3px; animation-delay: 0.8s; box-shadow: 0 0 8px 2px rgba(255,255,255,0.9);"></div>
            <div class="star-dot" style="top: 25%; right: 25%; width: 5px; height: 5px; animation-delay: 1.0s;"></div>
        </div>

        <div class="aether-layout">
            <div class="top-zone">
                <div class="disc-orb">
                    <div class="disc-orbit"></div>
                    <div class="disc-spin" id="album-art"></div>
                </div>
                <div class="info-bubble">
                    <div class="song-title" id="disp-title">加载中...</div>
                    <div class="artist-name" id="disp-artist">--</div>
                </div>
            </div>

            <div class="decor-line"></div>

            <div class="lyrics-zone">
                <div class="lyrics-bubble">
                    <div class="lyrics-text" id="disp-lyric">“ ... ”</div>
                </div>
            </div>

            <div class="control-pod">
                <audio class="design-audio" id="main-audio" controls controlsList="nodownload">
                    您的浏览器不支持Audio标签
                </audio>
            </div>
        </div>
    </div>

    <!-- 新增：延展控制面板 -->
    <div class="extension-panel" id="ext-panel">
        <div class="panel-toggle" id="panel-toggle-btn">▼ 展开播放列表</div>
        
        <div class="ext-controls">
            <button class="ext-btn" id="btn-prev">⏮ 上一首</button>
            <button class="ext-btn" id="btn-mode">🔁 顺序播放</button>
            <button class="ext-btn" id="btn-next">⏭ 下一首</button>
        </div>

        <ul class="playlist-ul" id="playlist-container">
            <!-- JS生成列表 -->
        </ul>
    </div>
</div>

<script>
document.addEventListener('DOMContentLoaded', () => {
    // ==========================================
    // 【配置区域】请在这里填入你的歌曲
    // ==========================================
    const songs = [
        { 
            title: "痕迹器官", 
            artist: "JEMN", 
            lyric: "把爱意藏在痕迹里",
            src: "https://files.catbox.moe/1ymem4.mp3" 
        },
        { 
            title: "Luv(sic) Pt.6", 
            artist: "Nujabes", 
            lyric: "Just pass it on like a song",
            src: "https://files.catbox.moe/wlw7g2.mp3" 
        },
        { 
            title: "Summertime", 
            artist: "Beyoncé", 
            lyric: "When you touched my hand",
            src: "https://files.catbox.moe/dn3564.mp3" 
        },
        { 
            title: "my leaving", 
            artist: "Mabanua", 
            lyric: "Keep walking straight ahead",
            src: "https://files.catbox.moe/cd4m2t.mp3" 
        },
        { 
            title: "You&I", 
            artist: "Avant", 
            lyric: "I wanna be where you are",
            src: "https://files.catbox.moe/qqs5ox.mp3" 
        },
        { 
            title: "Almost", 
            artist: "Tamia", 
            lyric: "can you tell me",
            src: "https://files.catbox.moe/q8m0r3.mp3" 
        },
        { 
            title: "falling out", 
            artist: "Cole", 
            lyric: "Don't let your pride get in the way",
            src: "https://files.catbox.moe/rcppgx.mp3" 
        },
        { 
            title: "our love", 
            artist: "Mabanua", 
            lyric: "our love is stronger than that ",
            src: "https://files.catbox.moe/okmiqc.mp3" 
        }
        // 复制上面的块 {...} 粘贴添加更多歌曲
    ];

    // ==========================================
    // 【核心逻辑】不需要修改以下代码
    // ==========================================
    const audio = document.getElementById('main-audio');
    const domTitle = document.getElementById('disp-title');
    const domArtist = document.getElementById('disp-artist');
    const domLyric = document.getElementById('disp-lyric');
    const domDisc = document.getElementById('album-art');
    const playlistContainer = document.getElementById('playlist-container');
    const btnMode = document.getElementById('btn-mode');
    const panel = document.getElementById('ext-panel');
    const toggleBtn = document.getElementById('panel-toggle-btn');

    let currentIndex = 0;
    let playMode = 0; // 0: 顺序, 1: 单曲循环, 2: 随机
    const modes = [
        { name: "🔁 顺序播放", loop: false },
        { name: "🔂 单曲循环", loop: true },
        { name: "🔀 随机播放", loop: false }
    ];

    // 1. 初始化加载
    function loadSong(index, autoPlay = false) {
        if(index < 0 || index >= songs.length) return;
        
        const song = songs[index];
        currentIndex = index;

        domTitle.innerText = song.title;
        domArtist.innerText = song.artist;
        domLyric.innerText = `“ ${song.lyric} ”`;
        
        // 切换源时不重置播放器，除非源真的变了
        if(audio.src !== song.src) {
            audio.src = song.src;
        }

        updatePlaylistHighlight();
        
        if(autoPlay) {
            audio.play().catch(e => console.log("自动播放拦截", e));
            domDisc.classList.add('playing');
        } else {
            domDisc.classList.remove('playing');
        }
    }

    // 2. 渲染播放列表
    function renderPlaylist() {
        playlistContainer.innerHTML = '';
        songs.forEach((song, idx) => {
            const li = document.createElement('li');
            li.className = 'playlist-li';
            li.innerHTML = `<span>${song.title}</span> <span style="font-size:10px; opacity:0.7">${song.artist}</span>`;
            li.onclick = () => {
                loadSong(idx, true);
            };
            playlistContainer.appendChild(li);
        });
        updatePlaylistHighlight();
    }

    function updatePlaylistHighlight() {
        const items = playlistContainer.querySelectorAll('.playlist-li');
        items.forEach((item, idx) => {
            if(idx === currentIndex) item.classList.add('active');
            else item.classList.remove('active');
        });
    }

    // 3. 播放控制逻辑
    function nextSong(isAuto = false) {
        let nextIndex;
        if (playMode === 2) { // 随机
            // 简单的随机算法，防止连续重复
            do {
                nextIndex = Math.floor(Math.random() * songs.length);
            } while (songs.length > 1 && nextIndex === currentIndex);
        } else {
            nextIndex = (currentIndex + 1) % songs.length;
        }
        loadSong(nextIndex, true);
    }

    function prevSong() {
        let prevIndex = (currentIndex - 1 + songs.length) % songs.length;
        loadSong(prevIndex, true);
    }

    // 4. 事件监听
    // 自动下一首 (丝滑体验的核心)
    audio.addEventListener('ended', () => {
        if (playMode === 1) {
            audio.play(); // 单曲循环模式手动重播
        } else {
            nextSong(true);
        }
    });

    audio.addEventListener('play', () => domDisc.classList.add('playing'));
    audio.addEventListener('pause', () => domDisc.classList.remove('playing'));

    document.getElementById('btn-next').onclick = () => nextSong();
    document.getElementById('btn-prev').onclick = () => prevSong();
    
    btnMode.onclick = () => {
        playMode = (playMode + 1) % modes.length;
        btnMode.innerText = modes[playMode].name;
        audio.loop = modes[playMode].loop; // 原生Loop属性辅助
    };

    // 5. 面板折叠动画
    toggleBtn.onclick = () => {
        panel.classList.toggle('expanded');
        toggleBtn.innerText = panel.classList.contains('expanded') ? "▲ 收起播放列表" : "▼ 展开播放列表";
    };

    // 启动
    renderPlaylist();
    loadSong(0, false); // 初始不自动播放，等待用户点击
});
</script>

</body>
</html>
