    const domLyric = document.getElementById('disp-lyric');
    const domDisc = document.getElementById('album-art');
    const playlistContainer = document.getElementById('playlist-container');
    const btnMode = document.getElementById('btn-mode');
    const panel = document.getElementById('ext-panel');
    const toggleBtn = document.getElementById('panel-toggle-btn');

    let currentIndex = 0;
    let playMode = 0; // 0: 顺序, 1: 单曲循环, 2: 随机
    const modes = [
        { name: "🔁 顺序播放", loop: false },
        { name: "🔂 单曲循环", loop: true },
        { name: "🔀 随机播放", loop: false }
