            src: "https://files.catbox.moe/okmiqc.mp3" 
        }
        // 复制上面的块 {...} 粘贴添加更多歌曲
    ];

    // ==========================================
    // 【核心逻辑】不需要修改以下代码
    // ==========================================
    const audio = document.getElementById('main-audio');
    const domTitle = document.getElementById('disp-title');
    const domArtist = document.getElementById('disp-artist');
    const domLyric = document.getElementById('disp-lyric');
    const domDisc = document.getElementById('album-art');
    const playlistContainer = document.getElementById('playlist-container');
    const btnMode = document.getElementById('btn-mode');
    const panel = document.getElementById('ext-panel');
    const toggleBtn = document.getElementById('panel-toggle-btn');

    let currentIndex = 0;
    let playMode = 0; // 0: 顺序, 1: 单曲循环, 2: 随机
    const modes = [
        { name: "🔁 顺序播放", loop: false },
        { name: "🔂 单曲循环", loop: true },
        { name: "🔀 随机播放", loop: false }
    ];

    // 1. 初始化加载
    function loadSong(index, autoPlay = false) {
        if(index < 0 || index >= songs.length) return;
        
        const song = songs[index];
        currentIndex = index;

        domTitle.innerText = song.title;
        domArtist.innerText = song.artist;
        domLyric.innerText = `“ ${song.lyric} ”`;
        
        // 切换源时不重置播放器，除非源真的变了
        if(audio.src !== song.src) {
            audio.src = song.src;
        }

        updatePlaylistHighlight();
        
        if(autoPlay) {
            audio.play().catch(e => console.log("自动播放拦截", e));
            domDisc.classList.add('playing');
        } else {
            domDisc.classList.remove('playing');
        }
    }

    // 2. 渲染播放列表
    function renderPlaylist() {
        playlistContainer.innerHTML = '';
        songs.forEach((song, idx) => {
            const li = document.createElement('li');
            li.className = 'playlist-li';
            li.innerHTML = `<span>${song.title}</span> <span style="font-size:10px; opacity:0.7">${song.artist}</span>`;
            li.onclick = () => {
                loadSong(idx, true);
            };
            playlistContainer.appendChild(li);
        });
        updatePlaylistHighlight();
    }

    function updatePlaylistHighlight() {
        const items = playlistContainer.querySelectorAll('.playlist-li');
        items.forEach((item, idx) => {
            if(idx === currentIndex) item.classList.add('active');
            else item.classList.remove('active');
        });
    }

    // 3. 播放控制逻辑
    function nextSong(isAuto = false) {
        let nextIndex;
        if (playMode === 2) { // 随机
            // 简单的随机算法，防止连续重复
            do {
                nextIndex = Math.floor(Math.random() * songs.length);
            } while (songs.length > 1 && nextIndex === currentIndex);
        } else {
            nextIndex = (currentIndex + 1) % songs.length;
        }
        loadSong(nextIndex, true);
    }

    function prevSong() {
        let prevIndex = (currentIndex - 1 + songs.length) % songs.length;
        loadSong(prevIndex, true);
    }

    // 4. 事件监听
    // 自动下一首 (丝滑体验的核心)
    audio.addEventListener('ended', () => {
        if (playMode === 1) {
            audio.play(); // 单曲循环模式手动重播
        } else {
            nextSong(true);
        }
    });

    audio.addEventListener('play', () => domDisc.classList.add('playing'));
    audio.addEventListener('pause', () => domDisc.classList.remove('playing'));

    document.getElementById('btn-next').onclick = () => nextSong();
    document.getElementById('btn-prev').onclick = () => prevSong();
    
    btnMode.onclick = () => {
        playMode = (playMode + 1) % modes.length;
        btnMode.innerText = modes[playMode].name;
        audio.loop = modes[playMode].loop; // 原生Loop属性辅助
    };

    // 5. 面板折叠动画
    toggleBtn.onclick = () => {
        panel.classList.toggle('expanded');
        toggleBtn.innerText = panel.classList.contains('expanded') ? "▲ 收起播放列表" : "▼ 展开播放列表";
    };

    // 启动
    renderPlaylist();
    loadSong(0, false); // 初始不自动播放，等待用户点击
});
</script>

</body>
</html>
