<script>
    const logContainer = document.getElementById('log-container');
    const commandInput = document.getElementById('custom-command');
    let logCounter = 0;

    function addLog(message) {
        logCounter++;
        const entry = document.createElement('div');
        entry.className = 'log-entry';
        entry.style.animationDelay = `${(logCounter % 5) * 0.1}s`;
        entry.innerHTML = `<span class="variable">[LOG_${logCounter}]</span>: <span class="string">${message}</span>`;
        logContainer.appendChild(entry);
        logContainer.scrollTop = logContainer.scrollHeight;
    }

    function executeCommand(type) {
        let command = '';
        switch(type) {
            case 'cheek':
                command = "ADD_DECAL('脸颊/数据矩阵贴片');";
                break;
            case 'hair':
                command = "APPLY_HAIR_DYE('发尾/霓虹青色');";
                break;
            case 'tattoo':
                command = "PRINT_TATTOO('锁骨/电路图');";
                break;

            case 'custom':
                command = commandInput.value;
                if (!command) {
                    addLog("[错误]: 自定义指令为空。");
                    return;
                }
