                endGame("WIN");
            }
            hpBar.style.width = currentHp + '%';
            hpValue.innerText = currentHp;
            updateScene(scenes[index]);
        }

        function endGame(result) {
            hpBar.style.width = '0%';
            hpValue.innerText = 0;
            resultOverlay.style.display = 'flex';
