    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>江惊闲</title>
    <!-- 引入外部字体以增强RPG感 -->
    <link rel="preconnect" href="https://fonts.googleapis.com">
    <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>
    <link href="https://fonts.googleapis.com/css2?family=Cinzel:wght@700&family=Noto+Serif+SC:wght@400;700&display=swap" rel="stylesheet">
    <style>
        /* CSS变量定义，用于统一管理配色和风格 */
        :root {
            --bg-main: #0c0a1a; /* 深邃的星空紫黑 */
            --bg-card: rgba(28, 25, 52, 0.85); /* 半透明的卡片背景 */
            --text-main: #e0e0e0; /* 柔和的白色文字 */
            --text-title: #f5d4a9; /* 华丽的金色标题 */
            --border-color: #5a4a8c; /* 紫水晶边框色 */
            --border-hover: #e7c5ff; /* 悬停时的高亮边框 */
            --glow-color: rgba(192, 160, 255, 0.6); /* 辉光效果 */
            --font-rpg: 'Cinzel', serif; /* RPG风格的西文标题字体 */
            --font-body: 'Noto Serif SC', serif; /* 优雅的中文宋体 */
            --transition-speed: 0.4s;
        }

        /* 基础样式重置 */
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        body {
            font-family: var(--font-body);
            background-color: var(--bg-main);
            color: var(--text-main);
            padding: clamp(15px, 3vw, 40px); /* 响应式内外边距 */
            display: flex;
            justify-content: center;
            align-items: flex-start; /* 改为flex-start以适应长内容 */
            min-height: 100vh;
            /* 背景增加星空闪烁效果 */
            background-image:
                radial-gradient(white, rgba(255, 255, 255, 0.2) 2px, transparent 40px),
                radial-gradient(white, rgba(255, 255, 255, 0.15) 1px, transparent 30px),
                radial-gradient(white, rgba(255, 255, 255, 0.1) 2px, transparent 40px),
                radial-gradient(rgba(255, 255, 255, 0.4), rgba(255, 255, 255, 0.1) 2px, transparent 30px);
            background-size: 550px 550px, 350px 350px, 250px 250px, 150px 150px;
            background-position: 0 0, 40px 60px, 130px 270px, 70px 100px;
            animation: stars-move 180s linear infinite;
        }

        @keyframes stars-move {
            from { background-position: 0 0, 40px 60px, 130px 270px, 70px 100px; }
            to { background-position: -10000px 5000px, -10000px 5000px, -10000px 5000px, -10000px 5000px; }
        }

        /* 主容器，采用玻璃拟态效果 */
        .main-container {
            width: 100%;
            max-width: 1800px; /* 增加最大宽度以适应更多栏 */
            background: var(--bg-card);
            border: 1px solid var(--border-color);
            border-radius: 20px;
            box-shadow: 0 0 30px var(--glow-color);
            padding: clamp(20px, 4vw, 50px);
            backdrop-filter: blur(10px);
            -webkit-backdrop-filter: blur(10px);
            position: relative;
            overflow: hidden;
        }

        /* 标题部分 */
        .header-title {
            text-align: center;
            margin-bottom: 40px;
        }

        .header-title h1 {
            font-family: var(--font-rpg);
            font-size: clamp(2.5em, 5vw, 4em);
            color: var(--text-title);
            font-weight: 700;
            letter-spacing: 5px;
            text-shadow: 0 0 5px var(--text-title), 0 0 15px var(--glow-color);
            animation: title-glow 3s infinite alternate;
        }

        .header-title h2 {
            font-family: var(--font-body);
            font-size: clamp(1em, 2vw, 1.2em);
            color: var(--text-main);
            margin-top: 10px;
            font-weight: 400;
            opacity: 0.8;
        }

        @keyframes title-glow {
            from { text-shadow: 0 0 5px var(--text-title), 0 0 15px var(--glow-color); }
            to { text-shadow: 0 0 10px var(--text-title), 0 0 25px var(--glow-color); }
        }

        /* 开场白列表容器，采用Grid布局 */
        .opening-grid {
            display: grid;
            /*--注意，此处我不应该使用固定栏数--> 采用响应式布局，自动适应屏幕宽度 */
            grid-template-columns: repeat(auto-fit, minmax(180px, 1fr));
            gap: clamp(15px, 2vw, 25px);
            padding: 20px 0;
        }

        /* 开场白卡片项目 */
        .opening-item {
            background: rgba(0,0,0,0.3);
            border: 1px solid var(--border-color);
            border-radius: 12px;
            padding: 20px;
            text-align: center;
            cursor: pointer;
            position: relative;
            overflow: hidden;
            transition: transform var(--transition-speed) ease, box-shadow var(--transition-speed) ease;
            opacity: 0;
            transform: translateY(30px);
            animation: fade-in-up 0.5s ease-out forwards;
            animation-delay: calc(var(--index) * 0.05s); /* 交错入场动画 */
        }

        @keyframes fade-in-up {
            to {
                opacity: 1;
                transform: translateY(0);
            }
        }

        .opening-item:hover {
            transform: translateY(-8px);
            border-color: var(--border-hover);
            box-shadow: 0 0 20px var(--glow-color);
        }

        /* 卡片装饰性伪元素 */
        .opening-item::before, .opening-item::after {
            content: '';
            position: absolute;
            opacity: 0.5;
            transition: opacity var(--transition-speed);
        }

        .opening-item::before { /* 左上角装饰 */
            top: 10px;
            left: 10px;
            width: 15px;
            height: 15px;
            border-top: 2px solid var(--text-title);
            border-left: 2px solid var(--text-title);
        }

        .opening-item::after { /* 右下角装饰 */
            bottom: 10px;
            right: 10px;
            width: 15px;
            height: 15px;
            border-bottom: 2px solid var(--text-title);
            border-right: 2px solid var(--text-title);
        }

        .opening-item:hover::before, .opening-item:hover::after {
            opacity: 1;
        }

        /* 卡片内容 */
        .item-title {
            font-family: var(--font-body);
            font-weight: 700;
            font-size: clamp(1.1em, 1.5vw, 1.2em);
            color: var(--text-title);
            margin-bottom: 12px;
            /* 确保长标题能正确换行 */
            white-space: normal;
            word-wrap: break-word;
        }

        .item-desc {
            font-size: clamp(0.85em, 1.2vw, 0.9em);
            color: var(--text-main);
            opacity: 0.7;
            line-height: 1.6;
            height: 80px; /* 固定高度，超出部分显示省略号 */
            overflow: hidden;
            text-overflow: ellipsis;
            display: -webkit-box;
            -webkit-line-clamp: 4; /* 最多显示4行，确保简介不会过长破坏布局 */
            -webkit-box-orient: vertical;
        }

        /* 底部版权信息 */
        .footer-notes {
            text-align: center;
            margin-top: 40px;
            padding-top: 20px;
            border-top: 1px solid var(--border-color);
            font-size: 0.8em;
            color: var(--text-main);
            opacity: 0.6;
            line-height: 1.8;
        }

    </style>
</head>
<body>
    <div class="main-container">

        <header class="header-title">
            <h1>Jiang Jingxian</h1>
            <h2>- Authored by 温允 -</h2>
        </header>

        <!-- 开场白网格布局 -->
        <main class="opening-grid" id="opening-grid-container">
            <!-- 这个容器将由JavaScript动态填充19个开场白按钮 -->
        </main>

        <footer class="footer-notes">
            <p>注意事项：此卡有世界书有正则有qr，禁止二传，禁止商用。</p>
            <p>如果买到就是被骗了，宝宝玩得开心！</p>
        </footer>

    </div>

    <script>
        // --- 交互逻辑 ---

        // 开场白数据，直接从您的要求中提取
        const openingsData = [
            { title: "以身相许", desc: "user获救后被要求以身相许" },
            { title: "网黄入警局", desc: "网黄user进警局" },
            { title: "老夫老妻", desc: "小手机：你做的饭我不吃" },
            { title: "兄弟妻", desc: "兄弟妻，不客气" },
            { title: "警校讲座", desc: "警校学生user听讲座" },
            { title: "警官加班", desc: "警官user加班" },
            { title: "亡命追击", desc: "警察user追犯人" },
            { title: "摸鱼日常", desc: "小警察user摸鱼" },
            { title: "游轮谜案", desc: "便衣警察user和江惊闲一起登上游轮查案" },
            { title: "边境风云", desc: "边境普通人user看城里人" },
            { title: "恶毒‘女’二", desc: "传说中的恶毒女二" },
            { title: "竹马纪事", desc: "青梅竹马/竹马竹马" },
            { title: "骨科禁恋", desc: "骨科线" },
            { title: "警匪游戏", desc: "警匪线-黑帮但温良user" },
            { title: "巨星私生活", desc: "大明星user强势睡粉" },
            { title: "校园反霸凌", desc: "校园线-和江惊闲一起反霸凌吧！（打开校园线世界书）" },
            { title: "英雄救美", desc: "校园线-江惊闲救下被霸凌的user（打开校园线世界书）" },
            { title: "分手风波", desc: "user吵着要分手" },
            { title: "待载入…", desc: "空白开场白" }
        ];

        // 动态加载和创建开场白按钮
        async function loadOpenings() {
            const container = document.getElementById('opening-grid-container');
            if (!container) return;

            // 检查SillyTavern API是否存在
            if (typeof getChatMessages !== 'function') {
                container.innerHTML = '<p style="text-align:center; color: #ff6b6b;">错误：SillyTavern API未找到，无法加载开场白。</p>';
                return;
            }

            try {
                // <!--注意，此处我不应该假定所有开场白都存在-->
                const messages = await getChatMessages("0", { include_swipe: true });
                const swipes = messages && messages.length > 0 ? messages[0].swipes : null;

                if (!swipes || swipes.length <= 1) {
                    // 即使没有可跳转的开场，也显示所有按钮，只是它们不可交互或提示用户
                     if (typeof triggerSlash === 'function') {
                        triggerSlash('/echo severity=info 提示：尚未添加任何可跳转的开场白。但所有剧本位已为您备好。');
                    }
                }

                // 遍历我们预设的19个开场白数据
                for (let i = 0; i < openingsData.length; i++) {
                    const data = openingsData[i];
                    const swipeId = i + 1; // swipe的索引从1开始

                    let title = data.title;
                    let description = data.desc;

                    // 检查真实的swipe是否存在，如果存在，则尝试抓取其内部的title和desc覆盖默认值
                    // 这让用户可以自定义，也提供了默认显示
                    if (swipes && swipes[swipeId]) {
                        const content = swipes[swipeId];
                        const titleMatch = content.match(/<!--\\s*title:\\s*(.*?)\\s*-->/);
                        if (titleMatch) title = titleMatch[1].trim();

                        const descMatch = content.match(/<!--\\s*desc:\\s*(.*?)(?=\\s*-->)-->/s);
                        if (descMatch) description = descMatch[1].trim();
                    }

                    // 创建开场白卡片元素
                    const item = document.createElement('div');
                    item.className = 'opening-item';
                    item.style.setProperty('--index', i); // 用于交错动画延迟
                    item.innerHTML = `
                        <h3 class="item-title">${title}</h3>
                        <p class="item-desc">${description}</p>
                    `;

                    // 绑定点击事件，调用切换函数
                    item.addEventListener('click', (event) => {
                         // 只有当对应的swipe实际存在时，才执行跳转
                        if (swipes && swipes[swipeId]) {
                            switchToOpening(swipeId, event);
                        } else {
                            // 如果不存在，给出提示
                            if (typeof triggerSlash === 'function') {
                                triggerSlash(`/echo severity=warning 该开场白（${title}）尚未创建，请在角色卡中添加。`);
                            }
                        }
                    });

                    container.appendChild(item);
                }

            } catch (error) {
                console.error('加载开场白失败:', error);
                container.innerHTML = '<p style="text-align:center; color: #ff6b6b;">加载开场白时出错，请检查控制台信息。</p>';
            }
        }

        // 切换开场白的函数
        async function switchToOpening(swipeId, event) {
            // 在点击时禁用按钮，增加视觉反馈
            const card = event.currentTarget;
            if (card) {
                 card.style.opacity = '0.5';
                 card.style.pointerEvents = 'none';
                 card.style.cursor = 'default';
            }

            try {
                const messages = await getChatMessages("0", { include_swipe: true });
                if (messages && messages[0].swipes && messages[0].swipes[swipeId]) {
                    // 调用setChatMessage进行切换
                    await setChatMessage(messages[0].swipes[swipeId], 0, {
                        swipe_id: swipeId,
                        refresh: 'display_and_render_current'
                    });
                } else {
                     throw new Error(`开场白 #${swipeId} 未找到。`);
                }
            } catch (error) {
                console.error('切换开场白失败:', error);
                 if (typeof triggerSlash === 'function') {
                    triggerSlash(`/echo severity=error 切换失败: ${error.message}`);
                }
                // 如果失败，恢复按钮状态
                if(card){
                    card.style.opacity = '1';
                    card.style.pointerEvents = 'auto';
                    card.style.cursor = 'pointer';
                }
            }
        }

        // 页面加载完成后，自动执行开场白加载函数
        window.addEventListener('load', loadOpenings);

    </script>
</body>
</html>
