        </main>

        <footer class="footer-notes">
            <p>注意事项：此卡有世界书有正则有qr，禁止二传，禁止商用。</p>
            <p>如果买到就是被骗了，宝宝玩得开心！</p>
        </footer>

    </div>

    <script>
        // --- 交互逻辑 ---

        // 开场白数据，直接从您的要求中提取
        const openingsData = [
            { title: "以身相许", desc: "user获救后被要求以身相许" },
            { title: "网黄入警局", desc: "网黄user进警局" },
            { title: "老夫老妻", desc: "小手机：你做的饭我不吃" },
            { title: "兄弟妻", desc: "兄弟妻，不客气" },
            { title: "警校讲座", desc: "警校学生user听讲座" },
            { title: "警官加班", desc: "警官user加班" },
            { title: "亡命追击", desc: "警察user追犯人" },
            { title: "摸鱼日常", desc: "小警察user摸鱼" },
            { title: "游轮谜案", desc: "便衣警察user和江惊闲一起登上游轮查案" },
            { title: "边境风云", desc: "边境普通人user看城里人" },
            { title: "恶毒‘女’二", desc: "传说中的恶毒女二" },
            { title: "竹马纪事", desc: "青梅竹马/竹马竹马" },
            { title: "骨科禁恋", desc: "骨科线" },
            { title: "警匪游戏", desc: "警匪线-黑帮但温良user" },
            { title: "巨星私生活", desc: "大明星user强势睡粉" },
            { title: "校园反霸凌", desc: "校园线-和江惊闲一起反霸凌吧！（打开校园线世界书）" },
            { title: "英雄救美", desc: "校园线-江惊闲救下被霸凌的user（打开校园线世界书）" },
            { title: "分手风波", desc: "user吵着要分手" },
            { title: "待载入…", desc: "空白开场白" }
        ];

        // 动态加载和创建开场白按钮
        async function loadOpenings() {
            const container = document.getElementById('opening-grid-container');
            if (!container) return;

            // 检查SillyTavern API是否存在
            if (typeof getChatMessages !== 'function') {
                container.innerHTML = '<p style="text-align:center; color: #ff6b6b;">错误：SillyTavern API未找到，无法加载开场白。</p>';
                return;
            }

            try {
                // <!--注意，此处我不应该假定所有开场白都存在-->
                const messages = await getChatMessages("0", { include_swipe: true });
                const swipes = messages && messages.length > 0 ? messages[0].swipes : null;

                if (!swipes || swipes.length <= 1) {
                    // 即使没有可跳转的开场，也显示所有按钮，只是它们不可交互或提示用户
                     if (typeof triggerSlash === 'function') {
                        triggerSlash('/echo severity=info 提示：尚未添加任何可跳转的开场白。但所有剧本位已为您备好。');
                    }
                }

                // 遍历我们预设的19个开场白数据
                for (let i = 0; i < openingsData.length; i++) {
                    const data = openingsData[i];
                    const swipeId = i + 1; // swipe的索引从1开始

                    let title = data.title;
                    let description = data.desc;

                    // 检查真实的swipe是否存在，如果存在，则尝试抓取其内部的title和desc覆盖默认值
                    // 这让用户可以自定义，也提供了默认显示
                    if (swipes && swipes[swipeId]) {
                        const content = swipes[swipeId];
                        const titleMatch = content.match(/<!--\\s*title:\\s*(.*?)\\s*-->/);
                        if (titleMatch) title = titleMatch[1].trim();

                        const descMatch = content.match(/<!--\\s*desc:\\s*(.*?)(?=\\s*-->)-->/s);
                        if (descMatch) description = descMatch[1].trim();
                    }

                    // 创建开场白卡片元素
                    const item = document.createElement('div');
                    item.className = 'opening-item';
                    item.style.setProperty('--index', i); // 用于交错动画延迟
                    item.innerHTML = `
                        <h3 class="item-title">${title}</h3>
                        <p class="item-desc">${description}</p>
                    `;

                    // 绑定点击事件，调用切换函数
                    item.addEventListener('click', (event) => {
                         // 只有当对应的swipe实际存在时，才执行跳转
                        if (swipes && swipes[swipeId]) {
                            switchToOpening(swipeId, event);
                        } else {
                            // 如果不存在，给出提示
                            if (typeof triggerSlash === 'function') {
                                triggerSlash(`/echo severity=warning 该开场白（${title}）尚未创建，请在角色卡中添加。`);
                            }
                        }
                    });

                    container.appendChild(item);
                }

            } catch (error) {
                console.error('加载开场白失败:', error);
                container.innerHTML = '<p style="text-align:center; color: #ff6b6b;">加载开场白时出错，请检查控制台信息。</p>';
            }
        }

        // 切换开场白的函数
        async function switchToOpening(swipeId, event) {
            // 在点击时禁用按钮，增加视觉反馈
            const card = event.currentTarget;
            if (card) {
                 card.style.opacity = '0.5';
                 card.style.pointerEvents = 'none';
                 card.style.cursor = 'default';
            }

            try {
                const messages = await getChatMessages("0", { include_swipe: true });
                if (messages && messages[0].swipes && messages[0].swipes[swipeId]) {
                    // 调用setChatMessage进行切换
                    await setChatMessage(messages[0].swipes[swipeId], 0, {
                        swipe_id: swipeId,
                        refresh: 'display_and_render_current'
                    });
                } else {
                     throw new Error(`开场白 #${swipeId} 未找到。`);
                }
            } catch (error) {
                console.error('切换开场白失败:', error);
                 if (typeof triggerSlash === 'function') {
                    triggerSlash(`/echo severity=error 切换失败: ${error.message}`);
                }
                // 如果失败，恢复按钮状态
                if(card){
                    card.style.opacity = '1';
                    card.style.pointerEvents = 'auto';
                    card.style.cursor = 'pointer';
                }
            }
        }

        // 页面加载完成后，自动执行开场白加载函数
        window.addEventListener('load', loadOpenings);

    </script>
</body>
</html>
