                init() {
                    // --【优化】自动为歌曲分配ID，避免手动管理时出错 --
                    this.state.songs.forEach((song, index) => song.id = index);
                    this.bindEvents();
                    this.renderPlaylist();
                    this.loadSong(this.state.currentSongIndex, false);
                    this.updateUI();
                }

                bindEvents() {
                    this.container.addEventListener('click', (e) => {
                        const target = e.target;
                        const actionMap = {
                            '#prev-button': () => this.playPrev(),
                            '#next-button': () => this.playNext(),
                            '#playback-mode-button': () => this.togglePlaybackMode(),
                            '#playlist-toggle': () => this.togglePlaylistVisibility(),
                            '#play-selected-button': () => this.playSelected(),
                            '#clear-selection-button': () => this.clearSelection(),
                        };

                        for (const selector in actionMap) {
                            if (target.closest(selector)) {
                                actionMap[selector]();
                                return;
                            }
                        }

                        const songItem = target.closest('li[data-song-id]');
                        if(songItem){
                            const songId = parseInt(songItem.dataset.songId);
                            if(target.classList.contains('song-selector-icon')){
                                this.toggleSongSelection(songId);
                            } else {
                                this.playSongById(songId);
                            }
                        }
                    });

                    this.audioPlayer.addEventListener('ended', () => this.handleSongEnd());
                    this.audioPlayer.addEventListener('play', () => this.stopOtherPlayers());
                }

                stopOtherPlayers() {
                    document.querySelectorAll('audio').forEach(audio => {
                        if (audio !== this.audioPlayer && !audio.paused) {
                            audio.pause();
                        }
                    });
                }

                updateUI() {
                    requestAnimationFrame(() => {
                        this.container.querySelector('#playback-mode-button').textContent = this.playbackModeTexts[this.state.playbackMode];
                        this.audioPlayer.loop = this.state.playbackMode === 'repeat-one';

                        const playlist = this.state.playbackMode === 'selected-sequence' ? this.state.currentSelectedPlaylist : this.state.songs;
                        this.container.querySelector('#prev-button').disabled = playlist.length <= 1;
                        this.container.querySelector('#next-button').disabled = playlist.length <= 1;

                        this.renderPlaylist();
                    });
                }

                loadSong(songIndex, autoplay = true) {
                    const song = this.state.songs[songIndex];
                    if (!song) return;
                    this.currentSongTitleElement.textContent = song.title;
                    this.currentSongArtistElement.textContent = song.artist;
                    this.audioPlayer.src = song.src;
                    this.audioPlayer.load();
                    if (autoplay) {
                        this.stopOtherPlayers();
                        this.audioPlayer.play().catch(e => console.error("Autoplay failed:", e));
                    }
                    this.state.currentSongIndex = songIndex;
                    this.updateUI();
                }

                playSongById(songId){
                    const index = this.state.songs.findIndex(s => s.id === songId);
                    if(index !== -1){
                        this.loadSong(index);
                        if(this.state.playbackMode === 'selected-sequence'){
                            this.clearSelection();
                        }
                    }
                }

                // --【修复】重写了切歌逻辑，使其能正确处理所有播放模式 --
                playNext() {
                    const { songs, playbackMode } = this.state;
                    let playlist = playbackMode === 'selected-sequence' ? this.state.currentSelectedPlaylist : songs;
                    if (playlist.length === 0) return;

                    let nextSong;
                    const currentSong = this.getCurrentSong();

                    if (playbackMode === 'shuffle') {
                        let nextIndex;
                        do { nextIndex = Math.floor(Math.random() * songs.length); } while (songs.length > 1 && nextIndex === this.state.currentSongIndex);
                        nextSong = songs[nextIndex];
                    } else if (playbackMode === 'selected-sequence') {
                        const currentIndexInSelected = playlist.findIndex(s => s.id === currentSong.id);
                        nextSong = playlist[(currentIndexInSelected + 1) % playlist.length];
                    } else { // sequence
                        const currentIndexInAll = songs.findIndex(s => s.id === currentSong.id);
                        nextSong = songs[(currentIndexInAll + 1) % songs.length];
                    }

                    const nextSongIndexInAll = this.state.songs.findIndex(s => s.id === nextSong.id);
                    this.loadSong(nextSongIndexInAll);
                }

                playPrev() {
                    const { songs, playbackMode } = this.state;
                    let playlist = playbackMode === 'selected-sequence' ? this.state.currentSelectedPlaylist : songs;
                    if (playlist.length === 0) return;

                    let prevSong;
                    const currentSong = this.getCurrentSong();

                    if (playbackMode === 'shuffle') {
                        this.playNext(); // 随机模式下上一首等于下一首
                        return;
                    } else if (playbackMode === 'selected-sequence') {
                        const currentIndexInSelected = playlist.findIndex(s => s.id === currentSong.id);
                        prevSong = playlist[(currentIndexInSelected - 1 + playlist.length) % playlist.length];
                    } else { // sequence
                        const currentIndexInAll = songs.findIndex(s => s.id === currentSong.id);
                        prevSong = songs[(currentIndexInAll - 1 + songs.length) % songs.length];
                    }

                    const prevSongIndexInAll = this.state.songs.findIndex(s => s.id === prevSong.id);
                    this.loadSong(prevSongIndexInAll);
                }

                handleSongEnd() {
                    if (this.state.playbackMode !== 'repeat-one') {
                        this.playNext();
                    } else {
                        this.audioPlayer.play();
                    }
                }

                togglePlaybackMode() {
                    const modes = ['sequence', 'repeat-one', 'shuffle'];
                    let currentModeIndex = modes.indexOf(this.state.playbackMode);
                    if(this.state.playbackMode === 'selected-sequence') {
                        this.clearSelection();
                        currentModeIndex = -1; // 从列表播放切换到顺序播放
                    }
                    this.state.playbackMode = modes[(currentModeIndex + 1) % modes.length];
                    this.updateUI();
                }

                togglePlaylistVisibility() {
                    const playlistContainer = this.container.querySelector('.playlist-container');
                    playlistContainer.classList.toggle('collapsed');
                    const title = this.container.querySelector('.playlist-title');
                    title.classList.toggle('collapsed');
                    const span = title.querySelector('span');
                    span.textContent = title.classList.contains('collapsed') ? ' ►' : ' ▼';
                }

                toggleSongSelection(songId) {
                    const song = this.state.songs.find(s => s.id === songId);
                    const index = this.state.currentSelectedPlaylist.findIndex(s => s.id === songId);
                    if (index > -1) {
                        this.state.currentSelectedPlaylist.splice(index, 1);
                    } else {
                        this.state.currentSelectedPlaylist.push(song);
                    }
                    this.updateUI();
                }

                playSelected() {
                    if (this.state.currentSelectedPlaylist.length > 0) {
                        this.state.playbackMode = 'selected-sequence';
                        const firstSelectedSongId = this.state.currentSelectedPlaylist[0].id;
                        const songIndex = this.state.songs.findIndex(s => s.id === firstSelectedSongId);
                        this.loadSong(songIndex);
                    } else {
                        alert("请至少选择一首歌曲。");
                    }
                }

                clearSelection() {
                    this.state.currentSelectedPlaylist = [];
                    if (this.state.playbackMode === 'selected-sequence') {
                        this.state.playbackMode = 'sequence';
                    }
                    this.updateUI();
                }

                getCurrentSong() {
                    return this.state.songs[this.state.currentSongIndex] || {};
                }

                renderPlaylist() {
                    this.playlistElement.innerHTML = '';
                    const fragment = document.createDocumentFragment();
                    const currentSongId = this.getCurrentSong().id;

                    this.state.songs.forEach(song => {
                        const li = document.createElement('li');
                        li.dataset.songId = song.id;
                        // --【修复】确保在任何模式下，当前播放歌曲都能正确高亮 --
                        if (song.id === currentSongId) {
                            li.classList.add('active');
                        }

                        const isSelected = this.state.currentSelectedPlaylist.some(s => s.id === song.id);
                        li.innerHTML = `
                            <span class="song-selector-icon ${isSelected ? 'selected' : ''}"></span>
                            <div class="song-info">${song.title} - ${song.artist}</div>
                        `;
                        fragment.appendChild(li);
                    });
                    this.playlistElement.appendChild(fragment);
                }
            }

            new ApexMusicPlayer('#apex-music-player');
        });
    </script>
