    <script>
        class MusicPlayer {
            constructor() {
                this.audio = document.getElementById('audioPlayer');
                this.playBtn = document.getElementById('playBtn');
                this.playIcon = document.getElementById('playIcon');
                this.progressBar = document.getElementById('progressBar');
                this.progressFill = document.getElementById('progressFill');
                this.currentTimeEl = document.getElementById('currentTime');
                this.totalTimeEl = document.getElementById('totalTime');
                this.volumeSlider = document.getElementById('volumeSlider');
                this.playingAnimation = document.getElementById('playingAnimation');
                
                this.isPlaying = false;
                this.isDragging = false;
                
                this.init();
            }

            init() {
                // 设置初始音量
                this.audio.volume = 0.7;
                
                // 绑定事件监听器
                this.playBtn.addEventListener('click', () => this.togglePlay());
                this.progressBar.addEventListener('click', (e) => this.seek(e));
                this.volumeSlider.addEventListener('input', (e) => this.setVolume(e.target.value));
                
                // 音频事件
                this.audio.addEventListener('loadedmetadata', () => this.updateTotalTime());
                this.audio.addEventListener('timeupdate', () => this.updateProgress());
                this.audio.addEventListener('ended', () => this.onTrackEnd());
                this.audio.addEventListener('canplaythrough', () => this.onCanPlay());
                
                // 键盘控制
                document.addEventListener('keydown', (e) => {
                    if (e.code === 'Space' && e.target.tagName !== 'INPUT') {
                        e.preventDefault();
                        this.togglePlay();
                    }
                });

                // 尝试自动播放
                this.attemptAutoplay();
            }

            async attemptAutoplay() {
                try {
                    // 稍等片刻再尝试播放
                    setTimeout(async () => {
                        try {
                            await this.audio.play();
                            this.isPlaying = true;
                            this.updatePlayButton();
                        } catch (error) {
                            console.log('自动播放被阻止，需要用户交互');
                        }
                    }, 500);
                } catch (error) {
                    console.log('自动播放失败');
                }
            }

            async togglePlay() {
                try {
                    if (this.isPlaying) {
                        this.audio.pause();
                        this.isPlaying = false;
                    } else {
                        await this.audio.play();
                        this.isPlaying = true;
                    }
                    this.updatePlayButton();
                } catch (error) {
                    console.error('播放错误:', error);
                }
            }

            updatePlayButton() {
                if (this.isPlaying) {
                    this.playIcon.className = 'fas fa-pause';
                    this.playingAnimation.classList.add('active');
                } else {
                    this.playIcon.className = 'fas fa-play';
                    this.playingAnimation.classList.remove('active');
                }
            }

            seek(e) {
                const rect = this.progressBar.getBoundingClientRect();
                const clickX = e.clientX - rect.left;
                const percentage = clickX / rect.width;
                const newTime = percentage * this.audio.duration;
                
                if (!isNaN(newTime)) {
                    this.audio.currentTime = newTime;
                }
            }

            updateProgress() {
                if (!this.isDragging) {
                    const percentage = (this.audio.currentTime / this.audio.duration) * 100;
                    this.progressFill.style.width = percentage + '%';
                    this.currentTimeEl.textContent = this.formatTime(this.audio.currentTime);
                }
            }

            updateTotalTime() {
                this.totalTimeEl.textContent = this.formatTime(this.audio.duration);
            }

            setVolume(value) {
                this.audio.volume = value / 100;
            }

            formatTime(seconds) {
                if (isNaN(seconds)) return '0:00';
                const mins = Math.floor(seconds / 60);
                const secs = Math.floor(seconds % 60);
                return `${mins}:${secs.toString().padStart(2, '0')}`;
            }

            onTrackEnd() {
                // 由于设置了loop属性，音频会自动循环，这里只做必要的状态检查
                if (!this.audio.loop) {
                    this.isPlaying = false;
                    this.updatePlayButton();
                    this.progressFill.style.width = '0%';
                    this.currentTimeEl.textContent = '0:00';
                }
            }

            onCanPlay() {
                // 音频加载完成后的处理
                console.log('音频已准备好播放');
            }
        }

        // 初始化播放器
        document.addEventListener('DOMContentLoaded', () => {
            new MusicPlayer();
        });

