            border: none;
            box-shadow: 0 0 8px rgba(255, 51, 51, 0.6);
        }

        .playing-animation {
            position: absolute;
            top: 10px;
            right: 15px;
            display: none;
            gap: 2px;
        }

        .playing-animation.active {
            display: flex;
        }

        .bar {
            width: 3px;
            height: 12px;
            background: #ff3333;
            border-radius: 2px;
            animation: bounce 1.5s infinite;
        }

        .bar:nth-child(2) { animation-delay: 0.1s; }
        .bar:nth-child(3) { animation-delay: 0.2s; }
        .bar:nth-child(4) { animation-delay: 0.3s; }

        @keyframes bounce {
            0%, 100% { transform: scaleY(0.4); }
            50% { transform: scaleY(1); }
        }

        .status-indicator {
            position: absolute;
            top: 15px;
            left: 20px;
            width: 8px;
            height: 8px;
            border-radius: 50%;
            background: #ff3333;
            box-shadow: 0 0 10px rgba(255, 51, 51, 0.8);
            animation: pulse 2s infinite;
        }

        @keyframes pulse {
            0%, 100% { opacity: 0.6; }
            50% { opacity: 1; }
        }

        /* 响应式设计 */
        @media (max-width: 480px) {
            .music-player {
                padding: 20px;
                margin: 5px;
            }
            
            .controls {
                gap: 15px;
            }
            
            .play-btn {
                width: 55px;
                height: 55px;
                font-size: 22px;
            }
            
            .secondary-btn {
                width: 40px;
                height: 40px;
                font-size: 14px;
            }
        }
    </style>
</head>
<body>
    <div class="music-player-container">
        <div class="music-player">
            <div class="status-indicator"></div>
            
            <div class="playing-animation" id="playingAnimation">
                <div class="bar"></div>
                <div class="bar"></div>
                <div class="bar"></div>
                <div class="bar"></div>
            </div>

            <div class="player-info">
                <div class="song-title" id="songTitle">Midnight Sky</div>
                <div class="artist-name" id="artistName">"She makes everything feel so right."</div>
            </div>

            <div class="progress-container">
                <div class="progress-bar" id="progressBar">
                    <div class="progress-fill" id="progressFill"></div>
                </div>
                <div class="time-display">
                    <span id="currentTime">0:00</span>
                    <span id="totalTime">0:00</span>
                </div>
            </div>

            <div class="controls">
                <button class="control-btn secondary-btn" id="prevBtn" title="上一首">
                    <i class="fas fa-step-backward"></i>
                </button>
                
                <button class="control-btn play-btn" id="playBtn" title="播放/暂停">
                    <i class="fas fa-play" id="playIcon"></i>
                </button>
                
                <button class="control-btn secondary-btn" id="nextBtn" title="下一首">
                    <i class="fas fa-step-forward"></i>
                </button>
            </div>

            <div class="volume-container">
                <i class="fas fa-volume-down volume-icon"></i>
                <input type="range" class="volume-slider" id="volumeSlider" min="0" max="100" value="70">
                <i class="fas fa-volume-up volume-icon"></i>
            </div>

            <audio id="audioPlayer" preload="auto" loop>
                <source src="https://files.catbox.moe/zpblun.mp3" type="audio/mpeg">
            </audio>
        </div>
    </div>

    <script>
        class MusicPlayer {
            constructor() {
                this.audio = document.getElementById('audioPlayer');
                this.playBtn = document.getElementById('playBtn');
                this.playIcon = document.getElementById('playIcon');
                this.progressBar = document.getElementById('progressBar');
                this.progressFill = document.getElementById('progressFill');
                this.currentTimeEl = document.getElementById('currentTime');
                this.totalTimeEl = document.getElementById('totalTime');
                this.volumeSlider = document.getElementById('volumeSlider');
                this.playingAnimation = document.getElementById('playingAnimation');
                
                this.isPlaying = false;
                this.isDragging = false;
                
                this.init();
            }

            init() {
                // 设置初始音量
                this.audio.volume = 0.7;
                
                // 绑定事件监听器
                this.playBtn.addEventListener('click', () => this.togglePlay());
                this.progressBar.addEventListener('click', (e) => this.seek(e));
                this.volumeSlider.addEventListener('input', (e) => this.setVolume(e.target.value));
                
                // 音频事件
                this.audio.addEventListener('loadedmetadata', () => this.updateTotalTime());
                this.audio.addEventListener('timeupdate', () => this.updateProgress());
                this.audio.addEventListener('ended', () => this.onTrackEnd());
                this.audio.addEventListener('canplaythrough', () => this.onCanPlay());
                
                // 键盘控制
                document.addEventListener('keydown', (e) => {
                    if (e.code === 'Space' && e.target.tagName !== 'INPUT') {
                        e.preventDefault();
                        this.togglePlay();
                    }
                });

                // 尝试自动播放
                this.attemptAutoplay();
            }

            async attemptAutoplay() {
                try {
                    // 稍等片刻再尝试播放
                    setTimeout(async () => {
                        try {
                            await this.audio.play();
                            this.isPlaying = true;
                            this.updatePlayButton();
                        } catch (error) {
                            console.log('自动播放被阻止，需要用户交互');
                        }
                    }, 500);
                } catch (error) {
                    console.log('自动播放失败');
                }
            }

            async togglePlay() {
                try {
                    if (this.isPlaying) {
                        this.audio.pause();
                        this.isPlaying = false;
                    } else {
                        await this.audio.play();
                        this.isPlaying = true;
                    }
                    this.updatePlayButton();
                } catch (error) {
                    console.error('播放错误:', error);
                }
            }

            updatePlayButton() {
                if (this.isPlaying) {
                    this.playIcon.className = 'fas fa-pause';
                    this.playingAnimation.classList.add('active');
                } else {
                    this.playIcon.className = 'fas fa-play';
                    this.playingAnimation.classList.remove('active');
                }
            }

            seek(e) {
                const rect = this.progressBar.getBoundingClientRect();
                const clickX = e.clientX - rect.left;
                const percentage = clickX / rect.width;
                const newTime = percentage * this.audio.duration;
                
                if (!isNaN(newTime)) {
                    this.audio.currentTime = newTime;
                }
            }

            updateProgress() {
                if (!this.isDragging) {
                    const percentage = (this.audio.currentTime / this.audio.duration) * 100;
                    this.progressFill.style.width = percentage + '%';
                    this.currentTimeEl.textContent = this.formatTime(this.audio.currentTime);
                }
            }

            updateTotalTime() {
                this.totalTimeEl.textContent = this.formatTime(this.audio.duration);
            }

            setVolume(value) {
                this.audio.volume = value / 100;
            }

            formatTime(seconds) {
                if (isNaN(seconds)) return '0:00';
                const mins = Math.floor(seconds / 60);
                const secs = Math.floor(seconds % 60);
                return `${mins}:${secs.toString().padStart(2, '0')}`;
            }

            onTrackEnd() {
                // 由于设置了loop属性，音频会自动循环，这里只做必要的状态检查
                if (!this.audio.loop) {
                    this.isPlaying = false;
                    this.updatePlayButton();
                    this.progressFill.style.width = '0%';
                    this.currentTimeEl.textContent = '0:00';
                }
            }

            onCanPlay() {
                // 音频加载完成后的处理
                console.log('音频已准备好播放');
            }
        }

        // 初始化播放器
        document.addEventListener('DOMContentLoaded', () => {
            new MusicPlayer();
        });

        // 为SillyTavern优化 - 添加页面可见性检测
        document.addEventListener('visibilitychange', () => {
            const audio = document.getElementById('audioPlayer');
            if (document.hidden) {
                // 页面隐藏时保持播放
            } else {
                // 页面显示时恢复
            }
        });
    </script>

</body>
</html>
