 lyricsScroll.style.transform = `translateY(${offset}px)`;

 const lines = document.querySelectorAll('.lyric-line');
 lines.forEach((line, i) => {
 if (i === lyricIndex) line.classList.add('active');
 else line.classList.remove('active');
 });
 }
 }, 100);

