    text-shadow: 0 0 3px #000, 0 0 5px #0ff;
  }

  .danmaku:nth-child(1) { top: 0px; animation-delay: 0s; }
  .danmaku:nth-child(2) { top: 20px; animation-delay: 1.5s; }
  .danmaku:nth-child(3) { top: 40px; animation-delay: 3s; }
  .danmaku:nth-child(4) { top: 60px; animation-delay: 4.5s; }
  .danmaku:nth-child(5) { top: 80px; animation-delay: 6s; }

  @keyframes fly {
    0% { left: 100%; }
    100% { left: -100%; }
  }
</style>
