  <script>
    class SudachiParser {
      constructor(selector = 'sudachi') {
        this.dataElement = document.querySelector(selector);
        if (!this.dataElement) {
          console.error(`Sudachi Parser: Element with selector "${selector}" not found.`);
          this.data = [];
          return;
        }
        this.rawData = this.dataElement.textContent.trim();
        this.data = this.rawData.split('|').map(item => item.trim());
      }

      getData() {
        return {
          time: this.data[0] || '',
          date: this.data[1] || '',
          location: this.data[2] || '',
        };
      }
    }

    function updateUI(data) {
      document.getElementById('main-time').textContent = data.time;
      document.getElementById('date-info').textContent = data.date;
      document.getElementById('location-info').textContent = data.location;
      updateClock(data.time);
    }

    function updateClock(timeString) {
      if (!timeString || !timeString.includes(':')) return;

      const [hours, minutes] = timeString.split(':').map(Number);
      const hourHand = document.getElementById('hour-hand');
      const minuteHand = document.getElementById('minute-hand');

      // Calculate rotation
      // 12 hours = 360deg -> 1 hour = 30deg
      // 60 minutes = 360deg -> 1 minute = 6deg
      // Hour hand also moves based on minutes
      const hourRotation = (hours % 12) * 30 + minutes * 0.5;
      const minuteRotation = minutes * 6;

      if (hourHand) {
        hourHand.style.transform = `translateX(-50%) rotate(${hourRotation}deg)`;
      }
      if (minuteHand) {
        minuteHand.style.transform = `translateX(-50%) rotate(${minuteRotation}deg)`;
      }
    }

    document.addEventListener('DOMContentLoaded', () => {
      const parser = new SudachiParser();
      const content = parser.getData();
      updateUI(content);
    });
  </script>
</body>
