    <div class="status-card">
        <div class="header">CURRENT STATUS</div>
        <div class="content-grid">
            <div class="info-block">
                <div class="title">Time</div>
                <div class="data" id="status-time"></div>
            </div>
            <div class="info-block">
                <div class="title">Location</div>
                <div class="data" id="status-location"></div>
            </div>
            <div class="info-block">
                <div class="title">Weather</div>
                <div class="data" id="status-weather"></div>
            </div>
            <div class="info-block">
                <div class="title">Environment</div>
                <div class="data" id="status-environment"></div>
            </div>
        </div>
        <div class="attire-section">
            <div class="title">Attire</div>
            <ul id="attire-list">
                <!-- 内容将由 JavaScript 动态生成 -->
            </ul>
        </div>
