    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // 1. 获取所有需要填充数据的DOM元素
            const dataSource = document.getElementById('status-data-source');
            const timeEl = document.getElementById('status-time');
            const locationEl = document.getElementById('status-location');
            const weatherEl = document.getElementById('status-weather');
            const environmentEl = document.getElementById('status-environment');
            const attireListEl = document.getElementById('attire-list');

            // 2. 获取并处理原始文本数据
            const rawText = dataSource.textContent.trim();

            // 3. 定义正则表达式来捕捉各个部分
            // ([\s\S]*) 用于匹配包括换行符在内的所有字符
            const regex = /时间：(.*)\s*地点：(.*)\s*天气：(.*)\s*环境：(.*)\s*衣着：([\s\S]*)/;
            const match = rawText.match(regex);

            if (match) {
                // 4. 解构正则表达式的捕获组
                const [, time, location, weather, environment, attireBlock] = match;

                // 5. 填充顶部四个信息块
                timeEl.textContent = time.trim();
                locationEl.textContent = location.trim();
                weatherEl.textContent = weather.trim();
                environmentEl.textContent = environment.trim();

                // 6. 处理并填充“衣着”列表
                if (attireBlock && attireBlock.trim()) {
                    const attireLines = attireBlock.trim().split('\n');

                    const listItemsHtml = attireLines.map(line => {
                        line = line.trim();
                        if (!line) return ''; // 跳过空行

                        const colonIndex = line.indexOf('：');

                        // 检查是否存在冒号，并进行分割
                        if (colonIndex !== -1) {
                            const charName = line.substring(0, colonIndex);
                            const description = line.substring(colonIndex + 1);
                            // 返回格式化后的HTML字符串
                            return `<li><span class="char-name">${charName.trim()}：</span>${description.trim()}</li>`;
                        } else {
                            // 如果某一行没有冒号，则直接作为普通列表项返回
                            return `<li>${line}</li>`;
                        }
                    }).join(''); // 将数组中的所有HTML字符串连接成一个单一的字符串

                    attireListEl.innerHTML = listItemsHtml;
                }

            } else {
                console.error("数据格式不匹配，无法解析。请检查数据源格式是否正确。");
                // 可以在页面上显示错误提示
                document.querySelector('.status-card').innerHTML = '<div class="header">Error</div><p style="text-align:center;">数据格式错误，请检查！</p>';
            }
        });
    </script>

