            `;
        } else if (mode === 'excerpt') {
            // --- 购物小票模式 ---
            const now = new Date();
            const date = `${now.getFullYear()}/${String(now.getMonth() + 1).padStart(2, '0')}/${String(now.getDate()).padStart(2, '0')}`;
