    const renderContainer = document.getElementById('options-render-target');

    if (renderContainer) {
      individualOptions.forEach(optionText => {
        const cardDiv = document.createElement('div');
        cardDiv.className = 'option-card';
        cardDiv.textContent = optionText.trim();

        cardDiv.onclick = async () => {
          if (typeof triggerSlash === 'function') {
            await triggerSlash(`/send ${optionText.trim()}`);
            await triggerSlash(`/trigger`);
            // Remove the dialog from DOM instead of using /close
            document.querySelector('.container').remove();
          } else {
            console.log("SillyTavern API 'triggerSlash' not found. Action: /send " + optionText.trim());
          }
        };
        renderContainer.appendChild(cardDiv);
      });
    }

    function closeDialog() {
      // Remove the dialog from DOM instead of using /close
      document.querySelector('.container').remove();
    }
  </script>
