        .scene-content, .plot-content { padding-top: 10px; font-size: 0.95em; line-height: 1.8; }

        .seeds-details {
            border: var(--seeds-details-border); border-radius: 18px; overflow: hidden;
            background: var(--seeds-details-bg); box-shadow: var(--module-shadow);
