        .seeds-details:hover { box-shadow: 0 6px 20px var(--module-shadow); }
        .seeds-details summary {
            list-style: none; cursor: pointer; padding: 15px 20px; font-weight: 500;
            font-size: 1em; display: flex; justify-content: space-between; align-items: center;
            text-shadow: 0 1px 2px rgba(255,255,255,0.7); transition: background-color 0.3s ease;
        }
        body.dark-mode .seeds-details summary { text-shadow: 0 0 8px rgba(200, 168, 197, 0.3); }
        .seeds-details summary::-webkit-details-marker { display: none; }
        .seeds-details summary::after { content: var(--seeds-summary-arrow); font-size: 0.9em; color: var(--seeds-summary-arrow-color); transition: transform 0.4s cubic-bezier(0.83, 0, 0.17, 1); }
        .seeds-details[open] summary::after { transform: rotate(180deg) scale(1.2); }
        .seeds-content { padding: 0 25px 20px 25px; font-size: 0.95em; line-height: 1.8; border-top: 1px solid rgba(255, 255, 255, 0.3); }
        body.dark-mode .seeds-content { border-top: 1px solid rgba(200, 168, 197, 0.15); }
        .seeds-content p { margin: 15px 0; padding-left: 25px; position: relative; }
        .seeds-content p::before { content: var(--seeds-content-icon); position: absolute; left: 0; top: 2px; color: var(--seeds-content-icon-color); font-size: 1.1em; text-shadow: 0 0 5px var(--seeds-content-icon-color); transition: transform 0.3s; }
        .seeds-content p:hover::before { transform: rotate(15deg); }

        .meow-footer { text-align: center; padding: 20px 20px 10px 20px; font-size: 0.9em; color: var(--text-accent); position: relative; z-index: 2; }
        .meow-footer::before { content: ''; position: absolute; top: 0px; left: 20%; right: 20%; height: 1px; background: var(--footer-border); }
        .footer-text { font-family: 'Times New Roman', serif; letter-spacing: 1px; margin-top: 5px; }
        .footer-light { display: block; } .footer-dark { display: none; }
        body.dark-mode .footer-light { display: none; } body.dark-mode .footer-dark { display: block; }
        @keyframes floatBubble { 0%, 100% { transform: translateY(0) translateX(-50%); } 50% { transform: translateY(-3px) translateX(-50%); } }

        /* --- 主题切换开关 --- */
        .theme-switch-wrapper { display: flex; justify-content: center; align-items: center; margin-bottom: 15px;}
        .theme-switch { position: relative; display: inline-block; width: 60px; height: 34px; }
