            // --- Core Logic: Parsing, Typing, Scrolling ---

            /**
             * Parses multi-line text into a structured object.
             * @param {string} content - The raw text content.
             * @returns {{original: string, translation: string, source: string}|null}
             */
            function parseContent(content) {
                const lines = (content || '').split('\n').map(l => l.trim()).filter(Boolean);
                if (!lines.length) return null;
                // Last line is the source, remove leading dash variants
                const source = (lines.pop() || '').replace(/^[—―─-]\s*/, '').trim();
                return {
                    original: lines.length > 1 ? lines[0] : '',
                    translation: lines.length > 1 ? lines[1] : (lines[0] || ''),
                    source: source
                };
            }

            /**
             * Updates the state of navigation buttons based on scroll position.
