            function updateNavButtonsState() {
                const { scrollTop, scrollHeight, clientHeight } = elements.display;
                elements.scrollUpBtn.classList.toggle('disabled', scrollTop <= 0);
                elements.scrollDownBtn.classList.toggle('disabled', scrollTop + clientHeight >= scrollHeight - 1);
            }

            /**
             * Checks if the content overflows the display area and updates buttons.
