function handleFontPackFileSelected(event) {
    const file = event.target.files[0];
    if (!file) return;

    if (file.type !== 'application/json') {
        showNotification('请选择一个有效的 .json 字体包文件！');
        event.target.value = null;
        return;
    }

    const reader = new FileReader();
    reader.onload = (e) => {
        try {
            processImportedFontPackData(JSON.parse(e.target.result));
        } catch (err) {
            console.error('Font Manager: Error parsing font pack file:', err);
            showNotification('无法解析字体包文件。文件可能已损坏或格式不正确。');
        }
        event.target.value = null;
    };
    reader.onerror = () => {
        showNotification('读取字体包文件时发生错误。');
        event.target.value = null;
    };
    reader.readAsText(file);
}

function migrateFontPack(data) {
    // Placeholder for future migration logic.
    // For now, just return the data as is.
    console.log(`Font Manager: Checking font pack version. Pack: ${data.version}, Script: ${FONT_PACK_VERSION}`);
