        .item-row input[type="checkbox"]:checked + div .item-name {
            color: #495057;
            font-weight: 600;
        }

        .btn:focus {
            outline: none;
            box-shadow: 0 0 0 3px rgba(108, 117, 125, 0.2);
        }
    </style>
</head>
<body>
    <div class="card">
        <div class="header">
            <h1>预设打包助手</h1>
            <p>预设、正则与快速回复一键打包分享</p>
        </div>

        <div class="content">
            <div class="mode-tabs">
                <button class="tab-btn active" onclick="switchTab('author')">📦 打包</button>
                <button class="tab-btn" onclick="switchTab('player')">📥 导入</button>
            </div>

            <!-- 作者模式 -->
            <div id="author-tab" class="tab-content active">
                <div class="main-layout">
					<div class="section resource-section">
                        <div class="section-title">☑️ 选择要打包的资源</div>
                        <div class="selection-tabs">
                            <button id="select-tab-presets" class="selection-tab-btn active" onclick="switchSelectionTab('presets')">🎨 预设</button>
                            <button id="select-tab-regexes" class="selection-tab-btn" onclick="switchSelectionTab('regexes')">🔧 正则</button>
                            <button id="select-tab-quick-replies" class="selection-tab-btn" onclick="switchSelectionTab('quick-replies')">⚡ 快速回复</button>
                        </div>

                        <div id="selection-content-presets" class="selection-content active">
                            <div id="presets-list" class="list-box">
                                <div class="empty-state">加载中...</div>
                            </div>
                        </div>

						<div id="selection-content-regexes" class="selection-content">
                            <div id="regexes-list" class="list-box">
                                <div class="empty-state">加载中...</div>
                            </div>
                        </div>

                        <div id="selection-content-quick-replies" class="selection-content">
                            <div id="quick-reply-list" class="list-box">
                                <div class="empty-state">加载中...</div>
                            </div>
                        </div>
                    </div>

                    <div class="section config-section">
                        <div class="section-title">⚙️ 打包配置</div>
                        <div class="input-group">
                            <input type="text" id="tag-prefix" class="input-field" placeholder="标签前缀(可选)" style="flex: 1;" title="为所有导出项目添加统一前缀标识">
                            <input type="text" id="package-name" class="input-field" placeholder="打包文件名" style="flex: 2;" title="生成的JSON文件名称">
                        </div>

                        <div class="config-options">
                            <div class="option-group">
                                <div class="option-title">🔧 正则选项</div>
                                <div class="checkbox-option">
                                    <input type="checkbox" id="regex-no-overwrite">
                                    <label for="regex-no-overwrite">正则禁覆盖(添加"_新"后缀)</label>
                                </div>
                            </div>
                        </div>

                        <button class="btn btn-success" onclick="createPackage()">确定并生成打包文件</button>
                    </div>
                </div>
            </div>

            <!-- 玩家模式 -->
            <div id="player-tab" class="tab-content">
                <div class="player-content">
                    <div class="player-section">
                        <div class="section-title">📥 文件导入</div>
                        <div class="file-upload">
                            <input type="file" id="package-file" accept=".json" onchange="handleFileSelect(event)">
                            选择打包文件
                        </div>
                        <div id="package-info" class="package-info">
                            <h4>📋 包信息</h4>
                            <div id="package-details"></div>
                            <button class="btn btn-success" onclick="importPackage()" style="margin-top: 10px;">导入到酒馆</button>
                        </div>
                    </div>
                </div>
            </div>

            <div class="progress" id="progress">
                <div class="progress-bar" id="progress-bar"></div>
            </div>

            <div class="status-msg" id="status-msg"></div>
            <div class="debug-log" id="debug-log"></div>
        </div>
    </div>

    <script>
        let selectedPresets = [];
        let selectedRegexes = [];
        let selectedQuickReplySets = [];
        let packageData = null;

        function debugLog(message) {
            console.log('[打包助手]', message);
            const debugEl = document.getElementById('debug-log');
            if (debugEl) {
                debugEl.style.display = 'block';
                debugEl.innerHTML += new Date().toLocaleTimeString() + ': ' + message + '<br>';
                debugEl.scrollTop = debugEl.scrollHeight;
            }
        }

        function switchTab(tab) {
            document.querySelectorAll('.tab-btn').forEach(btn => btn.classList.remove('active'));
            document.querySelectorAll('.tab-content').forEach(content => content.classList.remove('active'));

            document.querySelector(`[onclick="switchTab('${tab}')"]`).classList.add('active');
            document.getElementById(`${tab}-tab`).classList.add('active');

            const debugEl = document.getElementById('debug-log');
            if (debugEl) {
                debugEl.style.display = 'none';
                debugEl.innerHTML = '';
            }
        }

        function switchSelectionTab(tab) {
            document.querySelectorAll('.selection-tab-btn').forEach(btn => btn.classList.remove('active'));
            document.querySelectorAll('.selection-content').forEach(content => content.classList.remove('active'));
            document.getElementById(`select-tab-${tab}`).classList.add('active');
            document.getElementById(`selection-content-${tab}`).classList.add('active');
        }

        function showStatus(message, type = 'info') {
            const statusEl = document.getElementById('status-msg');
            statusEl.textContent = message;
            statusEl.className = `status-msg status-${type}`;
            statusEl.style.display = 'block';

            setTimeout(() => {
                statusEl.style.display = 'none';
            }, 4000);

            debugLog(`状态: ${type.toUpperCase()} - ${message}`);
        }

        function showProgress(percent) {
            const progressEl = document.getElementById('progress');
            const progressBar = document.getElementById('progress-bar');

            progressEl.style.display = 'block';
            progressBar.style.width = percent + '%';

            if (percent >= 100) {
                setTimeout(() => {
                    progressEl.style.display = 'none';
                }, 800);
            }
        }

        async function loadPresets() {
            try {
                if (typeof TavernHelper === 'undefined') {
                    throw new Error('需要在SillyTavern环境中运行');
                }

                const presetNames = TavernHelper.getPresetNames();
                debugLog(`找到 ${presetNames.length} 个预设`);

                const container = document.getElementById('presets-list');
                container.innerHTML = '';

                if (presetNames.length === 0) {
                    container.innerHTML = '<div class="empty-state">未找到预设</div>';
                    return;
                }

                for (const name of presetNames) {
                    if (name === 'in_use') continue;

                    const preset = TavernHelper.getPreset(name);
                    if (!preset) continue;

                    const itemDiv = document.createElement('div');
                    itemDiv.className = 'item-row';
                    itemDiv.innerHTML = `
                        <input type="checkbox" id="preset-${name}" onchange="togglePreset('${name}')">
                        <div>
                            <div class="item-name">${name}</div>
                            <div class="item-desc">${preset.prompts ? preset.prompts.length : 0} 个提示词</div>
                        </div>
                    `;
                    container.appendChild(itemDiv);
                }
            } catch (error) {
                document.getElementById('presets-list').innerHTML = `<div class="empty-state">加载失败: ${error.message}</div>`;
                debugLog('预设加载错误: ' + error.stack);
            }
        }

        async function loadRegexes() {
            try {
                if (typeof TavernHelper === 'undefined') {
                    throw new Error('需要在SillyTavern环境中运行');
                }

                const regexes = TavernHelper.getTavernRegexes();
                debugLog(`找到 ${regexes.length} 个正则`);

                const container = document.getElementById('regexes-list');
                container.innerHTML = '';

                if (regexes.length === 0) {
                    container.innerHTML = '<div class="empty-state">未找到正则</div>';
                    return;
                }

                for (const regex of regexes) {
                    const itemDiv = document.createElement('div');
                    itemDiv.className = 'item-row';
                    itemDiv.innerHTML = `
                        <input type="checkbox" id="regex-${regex.id}" onchange="toggleRegex('${regex.id}')">
                        <div>
                            <div class="item-name">${regex.script_name}</div>
                            <div class="item-desc">${regex.find_regex.substring(0, 30)}...</div>
                        </div>
                    `;
                    container.appendChild(itemDiv);
                }
            } catch (error) {
                document.getElementById('regexes-list').innerHTML = `<div class="empty-state">加载失败: ${error.message}</div>`;
                debugLog('正则加载错误: ' + error.stack);
            }
        }

        async function loadQuickReplies() {
            try {
                if (typeof TavernHelper === 'undefined') {
                    throw new Error('需要在SillyTavern环境中运行');
                }
                const qrSetListJson = await TavernHelper.triggerSlash('/qr-set-list all');
                const qrSetNames = JSON.parse(qrSetListJson);
                debugLog(`找到 ${qrSetNames.length} 个快速回复集`);

                const container = document.getElementById('quick-reply-list');
                container.innerHTML = '';

                if (qrSetNames.length === 0) {
                    container.innerHTML = '<div class="empty-state">未找到快速回复集</div>';
                    return;
                }

                for (const name of qrSetNames) {
                    const qrListJson = await TavernHelper.triggerSlash(`/qr-list "${name}"`);
                    const qrList = JSON.parse(qrListJson);

                    const itemDiv = document.createElement('div');
                    itemDiv.className = 'item-row';
                    itemDiv.innerHTML = `
                        <input type="checkbox" id="qrset-${name}" onchange="toggleQuickReplySet('${name}')">
                        <div>
                            <div class="item-name">${name}</div>
                            <div class="item-desc">${qrList.length} 个回复</div>
                        </div>
                    `;
                    container.appendChild(itemDiv);
                }
            } catch (error) {
                document.getElementById('quick-reply-list').innerHTML = `<div class="empty-state">加载失败: ${error.message}</div>`;
                debugLog('快速回复加载错误: ' + error.stack);
            }
        }

        function togglePreset(name) {
            if (selectedPresets.includes(name)) {
                selectedPresets = selectedPresets.filter(n => n !== name);
            } else {
                selectedPresets.push(name);
            }
            debugLog(`预设选择: ${selectedPresets.length} 个`);
        }

        function toggleRegex(id) {
            if (selectedRegexes.includes(id)) {
                selectedRegexes = selectedRegexes.filter(n => n !== id);
            } else {
                selectedRegexes.push(id);
            }
            debugLog(`正则选择: ${selectedRegexes.length} 个`);
        }

        function toggleQuickReplySet(name) {
            if (selectedQuickReplySets.includes(name)) {
                selectedQuickReplySets = selectedQuickReplySets.filter(n => n !== name);
            } else {
                selectedQuickReplySets.push(name);
            }
            debugLog(`快速回复集选择: ${selectedQuickReplySets.length} 个`);
        }

        async function createPackage() {
            if (selectedPresets.length === 0 && selectedRegexes.length === 0 && selectedQuickReplySets.length === 0) {
                showStatus('请至少选择一个项目', 'error');
                return;
            }

            const packageName = document.getElementById('package-name').value.trim();
            if (!packageName) {
                showStatus('请输入包名称', 'error');
                return;
            }

            const tagPrefix = document.getElementById('tag-prefix').value.trim();
            const regexNoOverwrite = document.getElementById('regex-no-overwrite').checked;

            try {
                showStatus('生成打包文件...', 'info');
                showProgress(10);

                const packageObj = {
                    name: packageName,
                    created: new Date().toISOString(),
                    tag_prefix: tagPrefix,
                    presets: {},
                    regexes: {},
                    quick_reply_sets: {}
                };

                // 打包预设
                for (const presetName of selectedPresets) {
                    const preset = TavernHelper.getPreset(presetName);
                    if (preset) {
                        const finalName = tagPrefix ? `${tagPrefix}${presetName}` : presetName;
                        packageObj.presets[finalName] = preset;
                        debugLog(`已打包预设: ${finalName}`);
                    }
                }
                showProgress(30);

                // 打包正则
                const allRegexes = TavernHelper.getTavernRegexes();
                for (const regexId of selectedRegexes) {
                    const regex = allRegexes.find(r => r.id === regexId);
                    if (regex) {
                        let finalName = regex.script_name;
                        if (tagPrefix) {
                            finalName = `${tagPrefix}${finalName}`;
                        }
                        if (regexNoOverwrite) {
                            finalName = `${finalName}_新`;
                        }
                        const regexCopy = { ...regex, script_name: finalName };
                        delete regexCopy.id; // ID will be regenerated on import
                        packageObj.regexes[finalName] = regexCopy;
                        debugLog(`已打包正则: ${finalName}`);
                    }
                }
                showProgress(60);

                // 打包快速回复
                for (const setName of selectedQuickReplySets) {
                    const finalSetName = tagPrefix ? `${tagPrefix}${setName}` : setName;
                    const qrListJson = await TavernHelper.triggerSlash(`/qr-list "${setName}"`);
                    const qrList = JSON.parse(qrListJson);
                    const repliesArray = [];
                    for(const qrLabel of qrList) {
                        const qrDataJson = await TavernHelper.triggerSlash(`/qr-get set="${setName}" label="${qrLabel}"`);
                        const qrData = JSON.parse(qrDataJson);
                        repliesArray.push(qrData);
                    }
                    packageObj.quick_reply_sets[finalSetName] = repliesArray;
                    debugLog(`已打包快速回复集: ${finalSetName}`);
                }
                showProgress(80);

                const jsonStr = JSON.stringify(packageObj, null, 2);
                const blob = new Blob([jsonStr], { type: 'application/json' });
                const url = URL.createObjectURL(blob);

                const a = document.createElement('a');
                a.href = url;
                a.download = packageName + '.json';
                document.body.appendChild(a);
                a.click();
                document.body.removeChild(a);
                URL.revokeObjectURL(url);

                showProgress(100);
                showStatus('打包完成', 'success');
                debugLog(`打包完成: ${Object.keys(packageObj.presets).length} 预设, ${Object.keys(packageObj.regexes).length} 正则, ${Object.keys(packageObj.quick_reply_sets).length} 快速回复集`);
            } catch (error) {
                showProgress(100);
                showStatus('打包失败: ' + error.message, 'error');
                debugLog('打包错误: ' + error.stack);
            }
        }

        function handleFileSelect(event) {
            const file = event.target.files[0];
            if (!file) {
                debugLog('未选择文件');
                return;
            }

            debugLog(`选择文件: ${file.name}`);
            event.target.value = '';

            const reader = new FileReader();
            reader.onload = function(e) {
                try {
                    packageData = JSON.parse(e.target.result);
                    debugLog('文件解析成功');
                    displayPackageInfo(packageData);
                } catch (error) {
                    showStatus('文件格式错误', 'error');
                    debugLog('解析错误: ' + error.message);
                    packageData = null;
                    document.getElementById('package-info').style.display = 'none';
                }
            };

            reader.onerror = function() {
                showStatus('文件读取失败', 'error');
                debugLog('读取错误');
            };

            reader.readAsText(file);
        }

        function displayPackageInfo(data) {
            const detailsEl = document.getElementById('package-details');
            let html = `<p><strong>名称:</strong> ${data.name || '未知'}</p>`;
            html += `<p><strong>创建:</strong> ${data.created ? new Date(data.created).toLocaleString() : '未知'}</p>`;
            if (data.tag_prefix) {
                html += `<p><strong>标签:</strong> ${data.tag_prefix}</p>`;
            }
            html += `<p><strong>预设:</strong> ${data.presets ? Object.keys(data.presets).length : 0} 个</p>`;
            html += `<p><strong>正则:</strong> ${data.regexes ? Object.keys(data.regexes).length : 0} 个</p>`;
            html += `<p><strong>快速回复集:</strong> ${data.quick_reply_sets ? Object.keys(data.quick_reply_sets).length : 0} 个</p>`;

            detailsEl.innerHTML = html;
            document.getElementById('package-info').style.display = 'block';
            debugLog(`包信息: ${Object.keys(data.presets || {}).length} 预设, ${Object.keys(data.regexes || {}).length} 正则, ${Object.keys(data.quick_reply_sets || {}).length} 快速回复集`);
        }

        async function importPackage() {
            if (!packageData) {
                showStatus('请先选择文件', 'error');
                return;
            }

            try {
                showStatus('导入中...', 'info');
                let totalItems = (packageData.presets ? Object.keys(packageData.presets).length : 0) + 
                                 (packageData.regexes ? Object.keys(packageData.regexes).length : 0) +
                                 (packageData.quick_reply_sets ? Object.keys(packageData.quick_reply_sets).length : 0);
                let importedCount = 0;

                if (typeof TavernHelper === 'undefined') throw new Error('需要在SillyTavern环境中运行');

                // 导入预设
                if (packageData.presets) {
                    for (const [name, preset] of Object.entries(packageData.presets)) {
                        await TavernHelper.createOrReplacePreset(name, preset);
                        debugLog(`预设导入: ${name}`);
                    }
                }

                // 导入正则
                if (packageData.regexes && Object.keys(packageData.regexes).length > 0) {
                    const currentRegexes = TavernHelper.getTavernRegexes();
                    const newRegexes = [...currentRegexes];
                    for (const [name, regex] of Object.entries(packageData.regexes)) {
                        const regexToImport = { ...regex };
                        const existingIndex = newRegexes.findIndex(r => r.script_name === regexToImport.script_name);
                        if (existingIndex >= 0) {
                            regexToImport.id = newRegexes[existingIndex].id;
                            newRegexes[existingIndex] = regexToImport;
                            debugLog(`正则替换: ${regexToImport.script_name}`);
                        } else {
                            regexToImport.id = Date.now().toString() + Math.random().toString(36).substr(2, 9);
                            newRegexes.push(regexToImport);
                            debugLog(`正则新增: ${regexToImport.script_name}`);
                        }
                    }
                    await TavernHelper.replaceTavernRegexes(newRegexes, { scope: 'all' });
                }

                // 导入快速回复
                if (packageData.quick_reply_sets) {
                    for (const [setName, repliesArray] of Object.entries(packageData.quick_reply_sets)) {
                        await TavernHelper.triggerSlash(`/qr-set-create "${setName}"`);
                        for (const reply of repliesArray) {
                            const args = Object.entries(reply)
                                .filter(([key, value]) => key !== 'command' && value !== null && value !== undefined)
                                .map(([key, value]) => `${key}=${JSON.stringify(value)}`)
                                .join(' ');
                            const command = reply.command || '';
                            await TavernHelper.triggerSlash(`/qr-create set="${setName}" ${args} ${command}`);
                        }
                        debugLog(`快速回复集导入: ${setName}`);
                    }
                }

                showProgress(100);
                showStatus('导入完成！', 'success');
                debugLog('导入完成');

                packageData = null;
                document.getElementById('package-file').value = '';
                document.getElementById('package-info').style.display = 'none';

            } catch (error) {
                showProgress(100);
                showStatus('导入失败: ' + error.message, 'error');
                debugLog('导入错误: ' + error.stack);
            }
        }

        async function loadAllResources() {
            showStatus('正在加载所有资源...', 'info');
            showProgress(10);
            await loadPresets();
            showProgress(40);
            await loadRegexes();
            showProgress(70);
            await loadQuickReplies();
            showProgress(100);
            showStatus('资源加载完成', 'success');
        }

        document.addEventListener('DOMContentLoaded', function() {
            debugLog('打包助手初始化完成');
            loadAllResources();
        });
    </script>
</body>
</html>
```"
		}]);
	});
});

