function saveAutoPresetRulesToStorage() {
    try {
        safeLocalStorage.setItem(LOCAL_STORAGE_KEY_AUTO_PRESET_RULES, JSON.stringify(state.autoPresetRules));
        console.log('Font Manager: Auto preset rules saved to safeLocalStorage.');
    } catch (error) {
        console.error('Font Manager: Error saving auto preset rules to safeLocalStorage:', error);
    }
}

// 从 safeLocalStorage 加载自动切换规则
function loadAutoPresetRulesFromStorage() {
    try {
        const storedRules = safeLocalStorage.getItem(LOCAL_STORAGE_KEY_AUTO_PRESET_RULES);
        if (storedRules) {
            const parsedData = JSON.parse(storedRules);
            // Ensure the loaded data is an array before assigning it
            if (Array.isArray(parsedData)) {
                // Proactively assign unique IDs to any rules that don't have one for backward compatibility.
                let rulesPatched = false;
                state.autoPresetRules = parsedData.map(rule => {
                    if (!rule.id) {
                        rule.id = `rule-${Date.now()}-${Math.random().toString(36).slice(2, 7)}`;
                        rulesPatched = true;
                    }
                    return rule;
                });

                if (rulesPatched) {
                    console.log('Font Manager: Patched auto preset rules with missing IDs.');
                    saveAutoPresetRulesToStorage(); // Save the patched rules back to storage.
                }

                 console.log('Font Manager: Auto preset rules loaded from safeLocalStorage.', state.autoPresetRules);
            } else {
                console.warn('Font Manager: Corrupted auto preset rules data in safeLocalStorage. Resetting.');
                state.autoPresetRules = [];
            }
        } else {
            state.autoPresetRules = [];
            console.log('Font Manager: No auto preset rules found in safeLocalStorage.');
        }
    } catch (error) {
        console.error('Font Manager: Error loading auto preset rules from safeLocalStorage:', error);
        state.autoPresetRules = [];
    }

    try {
        const storedEnabled = safeLocalStorage.getItem(LOCAL_STORAGE_KEY_AUTO_PRESET_ENABLED);
        if (storedEnabled !== null) {
            state.autoPresetSwitchingEnabled = JSON.parse(storedEnabled);
        } else {
            state.autoPresetSwitchingEnabled = true;
        }
        console.log('Font Manager: Auto preset switching enabled state loaded:', state.autoPresetSwitchingEnabled);
    } catch (error) {
        console.error('Font Manager: Error loading auto preset switching enabled state:', error);
        state.autoPresetSwitchingEnabled = true;
    }
}


