function splitLines(s){
  return s ? s.trim().split('\n')
    .map(v=>v.trim().replace(/^(["â€œã€Œ])(.*)\1$/,'$2'))
    .filter(Boolean) : [];
}
function parsePrologue(s){
  if(!s || !s.trim()) return null;
  const lines = s.trim().split('\n');
