            </filter>
        </defs>
    </svg>

    <!-- æ•°æ®å±‚ -->
    <div class="raw-data" style="display: none;">$2
$3
$4
$5
$6</div>

    <div class="litho-container">
        <div class="litho-header">
            <div class="litho-meta">
                <span>Vol. VIII</span>
                <span>The Narrative Press</span>
                <span>Price: 5Â¢</span>
            </div>
            <div class="litho-title">$1</div>
            <div class="litho-subtitle">â€” Special Edition for Tate â€”</div>
        </div>

        <div class="litho-list" id="optionsList"></div>

        <div class="litho-controls">
            <button class="litho-btn" id="theme-btn">Invert</button>
            <button class="litho-btn active" id="mode-single">Single</button>
            <button class="litho-btn" id="mode-multi">Batch</button>
            <button class="litho-btn" id="btn-reset">Clear</button>
        </div>

        <button class="submit-block" id="btn-submit">PRINT & PUBLISH</button>
    </div>

    <div class="litho-toast" id="toastMsg"></div>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            const elements = {
                list: document.getElementById('optionsList'),
                raw: document.querySelector('.raw-data'),
                toast: document.getElementById('toastMsg'),
                btnTheme: document.getElementById('theme-btn'),
                btnSingle: document.getElementById('mode-single'),
                btnMulti: document.getElementById('mode-multi'),
                btnReset: document.getElementById('btn-reset'),
                btnSubmit: document.getElementById('btn-submit')
            };

            let state = { multi: false, selected: [] };

            const renderOptions = () => {
                const text = elements.raw.innerText;
                const lines = text.split('\n').filter(l => l.trim());
                const romans = ['I', 'II', 'III', 'IV', 'V', 'VI'];

                elements.list.innerHTML = '';
                lines.forEach((line, idx) => {
                    const el = document.createElement('div');
                    el.className = 'litho-option';
                    const label = romans[idx] || (idx + 1);

                    el.innerHTML = `
                        <div class="opt-idx">${label}.</div>
                        <div class="opt-text">${line.trim()}</div>
                    `;

                    el.addEventListener('click', () => handleSelect(el, line.trim()));
                    elements.list.appendChild(el);
                });
            };

            const handleSelect = (el, text) => {
                if (state.multi) {
                    if (el.classList.contains('chosen')) {
                        el.classList.remove('chosen');
                        state.selected = state.selected.filter(t => t !== text);
                    } else {
                        el.classList.add('chosen');
                        state.selected.push(text);
                    }
                    elements.btnSubmit.style.display = state.selected.length ? 'block' : 'none';
                } else {
                    document.querySelectorAll('.litho-option').forEach(o => o.classList.remove('chosen'));
                    el.classList.add('chosen');
                    injectText(text);
                }
            };

            const injectText = (text) => {
                try {
                    const textarea = window.top.document.querySelector('#send_textarea') || window.top.document.querySelector('textarea');
                    if (textarea) {
                        textarea.focus();
                        const prefix = textarea.value ? '\n' : '';
                        if (window.top.document.queryCommandSupported('insertText')) {
                            window.top.document.execCommand('insertText', false, prefix + text);
                        } else {
                            textarea.value += prefix + text;
                        }
                        textarea.dispatchEvent(new Event('input', { bubbles: true }));
                        textarea.scrollTop = textarea.scrollHeight;
                        showToast("TYPE SETTING COMPLETE.");
                    } else {
                        console.log("Output:", text);
                        showToast("ERROR: TRAY MISSING.");
                    }
                } catch (e) { console.error(e); }
            };

            const showToast = (msg) => {
                elements.toast.textContent = msg;
                elements.toast.classList.add('show');
                setTimeout(() => elements.toast.classList.remove('show'), 2000);
            };

            const toggleMode = (isMulti) => {
                state.multi = isMulti;
                state.selected = [];
                document.querySelectorAll('.litho-option').forEach(o => o.classList.remove('chosen'));
                elements.btnSubmit.style.display = 'none';
                elements.btnSingle.classList.toggle('active', !isMulti);
                elements.btnMulti.classList.toggle('active', isMulti);
                showToast(isMulti ? "MODE: BATCH PRINT" : "MODE: SINGLE PRINT");
            };

            elements.btnTheme.addEventListener('click', () => {
