        const targetUid = "14";              // è¯·æ›¿æ¢ä¸ºç”¨äºå­˜å‚¨æ•°æ®çš„æ¡ç›®UID
        // ===========================================
        const charName = "{{char}}"; // è‡ªåŠ¨è·å–å½“å‰è§’è‰²å
        function togglePanelTheme(btn) {
            const container = btn.closest('.succubus-skill-panel');
            container.classList.toggle('light-mode');
            const isLight = container.classList.contains('light-mode');
