        async function submitSkills() {
            if (typeof triggerSlash !== 'function') {
                alert("è¯·åœ¨é…’é¦†ç¯å¢ƒä¸­ä½¿ç”¨ã€‚");
                return;
            }
            // 1. è·å–é€‰ä¸­æ•°æ®
            const cards = document.querySelectorAll('.succubus-skill-panel .skill-card.selected');
            let skillTextArr = [];
            cards.forEach(c => {
                const name = c.querySelector('.skill-name').innerText;
                const desc = c.querySelector('.skill-desc').innerText;
                const buff = c.querySelector('.skill-buff').innerText;
                // æ ¼å¼åŒ–æ–°æŠ€èƒ½æ–‡æœ¬
                skillTextArr.push(`- ã€${name}ã€‘ï¼š${desc} (${buff})`);
            });
            const newSkillsBlock = skillTextArr.join('\n');
            // 2. æ˜¾ç¤ºLoading
            const overlay = document.getElementById('loadingOverlay');
            const loadingText = document.getElementById('loadingText');
            overlay.classList.add('show');
            try {
