                // æ³¨æ„ï¼šè¿™é‡Œéœ€è¦STæ”¯æŒ triggerSlash è¿”å›å€¼ã€‚
                loadingText.innerText = `æ­£åœ¨è¿æ¥ ${charName} çš„è®°å½•...`;
                const oldContent = await triggerSlash(`/getentryfield file="${targetFile}" field=content ${targetUid}`);

                // 4. æ­£åˆ™å¤„ç†ï¼šæŸ¥æ‰¾æ˜¯å¦å·²æœ‰è¯¥è§’è‰²çš„åŒºå—
                // åŒºå—æ ¼å¼å®šä¹‰ï¼š ### è§’è‰²å çš„æŠ€èƒ½ ### ... ###
                const regionStart = `### ${charName} çš„æŠ€èƒ½ ###`;
                const regionEnd = `### End ${charName} ###`;
                const regex = new RegExp(`${regionStart}([\\s\\S]*?)${regionEnd}`, 'i');

                let finalContent = "";

                if (oldContent.includes(regionStart)) {
                    // å¦‚æœå·²å­˜åœ¨è¯¥è§’è‰²çš„åŒºå—ï¼Œåˆ™åœ¨è¯¥åŒºå—å†…éƒ¨è¿½åŠ 
                    // ä¸ºäº†ç®€å•èµ·è§ï¼Œæˆ‘ä»¬æ›¿æ¢æ‰æ•´ä¸ªåŒºå—ä¸º æ—§å†…å®¹ + æ–°å†…å®¹
                    finalContent = oldContent.replace(regex, (match, p1) => {
                        return `${regionStart}${p1.trim()}\n${newSkillsBlock}\n${regionEnd}`;
                    });
                } else {
                    // å¦‚æœä¸å­˜åœ¨ï¼Œè¿½åŠ åˆ°æ–‡æœ«
                    // ç¡®ä¿æ—§å†…å®¹å­˜åœ¨ï¼Œå¦‚æœæ˜¯undefinedåˆ™ç½®ç©º
                    const safeOldContent = (oldContent && oldContent !== "undefined" && oldContent !== "null") ? oldContent : "";
                    finalContent = `${safeOldContent}\n\n${regionStart}\n${newSkillsBlock}\n${regionEnd}`;
                }

                // 5. å†™å…¥WI
                loadingText.innerText = "æ­£åœ¨åˆ»å°æ·«çº¹...";
                // å¿…é¡»å¯¹ finalContent è¿›è¡Œè½¬ä¹‰é˜²æ­¢ JSON æ ¼å¼é”™è¯¯ï¼Œä½† triggerSlash ä¼ å‚é€šå¸¸æ¯”è¾ƒå®½å®¹
                // åœ¨è¿™é‡Œæˆ‘ä»¬ç®€å•å¤„ç†ï¼Œå¦‚æœæœ‰æ¢è¡Œç¬¦ç­‰å¤æ‚æƒ…å†µæ¨è base64ï¼Œä½†STå¯ä»¥ç›´æ¥ä¼ 
                // ä¸ºäº†é¿å…ç®¡é“ç¬¦å†²çªï¼Œå¯ä»¥ä½¿ç”¨ setentryfield ... contents

                // æ„é€  ST Script å†™å…¥æ•´ä¸ªæ‹¼æ¥åçš„å­—ç¬¦ä¸²
                // æ³¨æ„ï¼šå¦‚æœå†…å®¹è¿‡é•¿å¯èƒ½ä¼šè¶…å‡ºå‘½ä»¤è¡Œé™åˆ¶ï¼Œå¦‚æœ‰é—®é¢˜è¯·åˆ†æ®µï¼Œä½†ä¸€èˆ¬å¤Ÿç”¨ã€‚
                await triggerSlash(`/setentryfield file="${targetFile}" uid=${targetUid} field=content ${finalContent}`);

                // 6. æˆåŠŸåé¦ˆ
                triggerSlash(`/echo severity=success åˆ»å°å®Œæˆï¼æ•°æ®å·²ä¿å­˜è‡³ [${targetFile}] æ¡ç›®ã€‚`);
                overlay.classList.remove('show');
                document.querySelector('.panel-frame').style.opacity = '0.5';
                document.getElementById('confirmSkillBtn').innerText = "åˆ»å°å®Œæˆ";

            } catch (e) {
                console.error(e);
                loadingText.innerText = "åˆ»å°å¤±è´¥ï¼Œè¯·æŸ¥çœ‹æ§åˆ¶å°";
                setTimeout(() => overlay.classList.remove('show'), 2000);
