    // è¿‡æ¸¡ç»“æŸåç§»é™¤ç¼“å†²ç±»
    setTimeout(()=>{
      card.classList.remove('theming');
      btn.classList.remove('pop');
    }, TWEEN_MS);
  });
}

card.addEventListener('click', (e)=>{
