        }

        function switchSelectionTab(tab) {
            document.querySelectorAll('.selection-tab-btn').forEach(btn => btn.classList.remove('active'));
            document.querySelectorAll('.selection-content').forEach(content => content.classList.remove('active'));
            document.getElementById(`select-tab-${tab}`).classList.add('active');
            document.getElementById(`selection-content-${tab}`).classList.add('active');
        }

        function showStatus(message, type = 'info') {
            const statusEl = document.getElementById('status-msg');
            statusEl.textContent = message;
            statusEl.className = `status-msg status-${type}`;
            statusEl.style.display = 'block';

            setTimeout(() => {
                statusEl.style.display = 'none';
            }, 4000);

            debugLog(`çŠ¶æ€: ${type.toUpperCase()} - ${message}`);
        }

        function showProgress(percent) {
            const progressEl = document.getElementById('progress');
            const progressBar = document.getElementById('progress-bar');

            progressEl.style.display = 'block';
            progressBar.style.width = percent + '%';

            if (percent >= 100) {
                setTimeout(() => {
                    progressEl.style.display = 'none';
                }, 800);
            }
        }

        async function loadPresets() {
            try {
                if (typeof TavernHelper === 'undefined') {
                    throw new Error('éœ€è¦åœ¨SillyTavernç¯å¢ƒä¸­è¿è¡Œ');
                }

                const presetNames = TavernHelper.getPresetNames();
                debugLog(`æ‰¾åˆ° ${presetNames.length} ä¸ªé¢„è®¾`);

                const container = document.getElementById('presets-list');
                container.innerHTML = '';

                if (presetNames.length === 0) {
                    container.innerHTML = '<div class="empty-state">æœªæ‰¾åˆ°é¢„è®¾</div>';
                    return;
                }

                for (const name of presetNames) {
                    if (name === 'in_use') continue;

                    const preset = TavernHelper.getPreset(name);
                    if (!preset) continue;

                    const itemDiv = document.createElement('div');
                    itemDiv.className = 'item-row';
                    itemDiv.innerHTML = `
                        <input type="checkbox" id="preset-${name}" onchange="togglePreset('${name}')">
                        <div>
                            <div class="item-name">${name}</div>
                            <div class="item-desc">${preset.prompts ? preset.prompts.length : 0} ä¸ªæç¤ºè¯</div>
                        </div>
                    `;
                    container.appendChild(itemDiv);
                }
            } catch (error) {
                document.getElementById('presets-list').innerHTML = `<div class="empty-state">åŠ è½½å¤±è´¥: ${error.message}</div>`;
                debugLog('é¢„è®¾åŠ è½½é”™è¯¯: ' + error.stack);
            }
        }

        async function loadRegexes() {
            try {
                if (typeof TavernHelper === 'undefined') {
                    throw new Error('éœ€è¦åœ¨SillyTavernç¯å¢ƒä¸­è¿è¡Œ');
                }

                const regexes = TavernHelper.getTavernRegexes();
                debugLog(`æ‰¾åˆ° ${regexes.length} ä¸ªæ­£åˆ™`);

                const container = document.getElementById('regexes-list');
                container.innerHTML = '';

                if (regexes.length === 0) {
                    container.innerHTML = '<div class="empty-state">æœªæ‰¾åˆ°æ­£åˆ™</div>';
                    return;
                }

                for (const regex of regexes) {
                    const itemDiv = document.createElement('div');
                    itemDiv.className = 'item-row';
                    itemDiv.innerHTML = `
                        <input type="checkbox" id="regex-${regex.id}" onchange="toggleRegex('${regex.id}')">
                        <div>
                            <div class="item-name">${regex.script_name}</div>
