// ========= Drag and Drop Handlers =========
function handleDragStart(event, index) {
    draggedItemIndex = index;
    event.dataTransfer.effectAllowed = 'move';
    event.target.classList.add('dragging-font-item');
}

function handleDragOver(event, targetIndex) {
    event.preventDefault();
    if (targetIndex !== draggedItemIndex) {
        const targetElement = event.target.closest('.font-manage-item');
        if (targetElement) {
            targetElement.classList.add('drag-over-font-item');
        }
    }
}

function handleDragLeave(event) {
    const targetElement = event.target.closest('.font-manage-item');
    if (targetElement) {
        targetElement.classList.remove('drag-over-font-item');
    }
}

function handleDrop(event, targetIndex) {
    event.preventDefault();
    const targetElement = event.target.closest('.font-manage-item');
    if (targetElement) {
        targetElement.classList.remove('drag-over-font-item');
    }

    if (draggedItemIndex !== null && draggedItemIndex !== targetIndex) {
        const itemToMove = state.importedFontFamilies.splice(draggedItemIndex, 1)[0];
        state.importedFontFamilies.splice(targetIndex, 0, itemToMove);
        saveFontsToStorage();
        renderCombinedMainView();
    }
    draggedItemIndex = null;
}

function handleDragEnd(event) {
    event.target.classList.remove('dragging-font-item');
    const allItems = modalBodyElement.querySelectorAll('.font-manage-item.drag-over-font-item');
    allItems.forEach(item => item.classList.remove('drag-over-font-item'));
    draggedItemIndex = null;
}

function deleteFont(index) {
    const deletedFont = state.importedFontFamilies.splice(index, 1)[0];
    let message = `å­—ä½“ \"${deletedFont}\" å·²åˆ é™¤ã€‚`;
    const activeApplied = state.activeFont || safeLocalStorage.getItem(LOCAL_STORAGE_KEY_ACTIVE_FONT);
    const computedFamily = (docContext && docContext.defaultView) ? docContext.defaultView.getComputedStyle(docContext.body).fontFamily : '';
    if (activeApplied === deletedFont || (computedFamily && computedFamily.toLowerCase().includes(deletedFont.toLowerCase()))) {
        resetToDefaultFont();
        message = `å­—ä½“ \"${deletedFont}\" å·²åˆ é™¤ï¼Œé¡µé¢å­—ä½“å·²é‡ç½®ä¸ºé»˜è®¤ã€‚`;
    }
    showNotification(message);
    saveFontsToStorage();
    renderCombinedMainView();
}

function handleExportFontPack() {
    const packData = {
        version: FONT_PACK_VERSION,
        timestamp: new Date().toISOString(),
        settings: {
            importedFontFamilies: state.importedFontFamilies,
            fontImportUrls: state.fontImportUrls,
            activeFont: safeLocalStorage.getItem(LOCAL_STORAGE_KEY_ACTIVE_FONT),
            globalFontSize: safeLocalStorage.getItem(LOCAL_STORAGE_KEY_GLOBAL_FONT_SIZE),
            globalFontWeight: safeLocalStorage.getItem(LOCAL_STORAGE_KEY_GLOBAL_FONT_WEIGHT),
            iconSize: safeLocalStorage.getItem(LOCAL_STORAGE_KEY_ICON_SIZE),
            buttonSize: safeLocalStorage.getItem(LOCAL_STORAGE_KEY_BUTTON_SIZE),
            hljsColors: {},
            hljsFontFamily: safeLocalStorage.getItem(LOCAL_STORAGE_KEY_HLJS_FONT_FAMILY),
            isHljsCustomizationEnabled: state.isHljsCustomizationEnabled,
            mesCodeBgColor: safeLocalStorage.getItem(LOCAL_STORAGE_KEY_MES_CODE_BG_COLOR),
            mesCodeTextColor: safeLocalStorage.getItem(LOCAL_STORAGE_KEY_MES_CODE_TEXT_COLOR),
            mesCodeBorderColor: safeLocalStorage.getItem(LOCAL_STORAGE_KEY_MES_CODE_BORDER_COLOR),
            mesCodeFontFamily: safeLocalStorage.getItem(LOCAL_STORAGE_KEY_MES_CODE_FONT_FAMILY),
            quoteTextColor: safeLocalStorage.getItem(LOCAL_STORAGE_KEY_QUOTE_TEXT_COLOR),
            quoteBgColor: safeLocalStorage.getItem(LOCAL_STORAGE_KEY_QUOTE_BG_COLOR),
            quoteBorderColor: safeLocalStorage.getItem(LOCAL_STORAGE_KEY_QUOTE_BORDER_COLOR),
            localFonts: state.localFonts,
            multilangEnabled: state.multilangEnabled,
            fontPresets: state.fontPresets,
            autoPresetRules: state.autoPresetRules,
            autoPresetSwitchingEnabled: state.autoPresetSwitchingEnabled,
            multilangFonts: {}
        },
        colorPalette: {}
    };

    Object.values(hljsScopeSettings).forEach(scope => {
        packData.settings.hljsColors[scope.lsKey] = safeLocalStorage.getItem(scope.lsKey);
    });
    Object.keys(colorSettings).forEach(key => {
        packData.colorPalette[key] = safeLocalStorage.getItem(colorSettings[key].lsKey);
    });

    Object.entries(languageTypes).forEach(([langKey, langConfig]) => {
        packData.settings.multilangFonts[langKey] = {
            fontFamily: state[langConfig.stateKey],
            fontUrl: state[langConfig.urlKey]
        };
    });

    const jsonString = JSON.stringify(packData, null, 2);
    const blob = new Blob([jsonString], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = docContext.createElement('a');
    a.href = url;
    a.download = `font-manager-pack-${new Date().toISOString().slice(0, 10)}.json`;
    docContext.body.appendChild(a);
    a.click();
    docContext.body.removeChild(a);
    URL.revokeObjectURL(url);
}

