function handleImportLocalFont() {
    const fileInput = docContext.getElementById('fontManagerLocalFontFileInput');
    const nameInput = docContext.getElementById('fontManagerLocalFontNameInput');
    const file = fileInput.files[0];
    const fontFamilyName = nameInput.value.trim();

    if (!file || !fontFamilyName) {
        showNotification('è¯·é€‰æ‹©ä¸€ä¸ªå­—ä½“æ–‡ä»¶å¹¶ä¸ºå…¶å‘½åã€‚', 'warning');
        return;
    }

    const MAX_FONT_SIZE = 15 * 1024 * 1024; // 15MB
    const ALLOWED_TYPES = ['font/ttf', 'font/otf', 'font/woff', 'font/woff2', 'application/font-sfnt'];
    const ALLOWED_EXTENSIONS = ['.ttf', '.otf', '.woff', '.woff2'];

    if (!ALLOWED_TYPES.includes(file.type) && !ALLOWED_EXTENSIONS.some(ext => file.name.toLowerCase().endsWith(ext))) {
        showNotification(`ä¸æ”¯æŒçš„æ–‡ä»¶ç±»å‹ã€‚è¯·é€‰æ‹© TTF, OTF, WOFF, æˆ– WOFF2 æ ¼å¼çš„å­—ä½“æ–‡ä»¶ã€‚`, 'error');
        return;
    }

    if (file.size > MAX_FONT_SIZE) {
        showNotification(`å­—ä½“æ–‡ä»¶è¿‡å¤§ (æœ€å¤§ ${MAX_FONT_SIZE / 1024 / 1024}MB)ã€‚`, 'error');
        return;
    }

    if (state.importedFontFamilies.includes(fontFamilyName) || state.localFonts.some(f => f.fontName === fontFamilyName)) {
        showNotification(`åç§° \"${fontFamilyName}\" å·²è¢«ä½¿ç”¨ã€‚`, 'warning');
        return;
    }

    const reader = new FileReader();
    reader.onload = (e) => {
        try {
            const fontDataUrl = e.target.result;
            const styleId = `font-manager-local-font-${fontFamilyName.replace(/\s+/g, '-')}`;
            const styleElement = docContext.createElement('style');
            styleElement.id = styleId;
            styleElement.textContent = `@font-face { font-family: '${fontFamilyName}'; src: url('${fontDataUrl}') format('truetype'); font-display: swap; }`;
            docContext.head.appendChild(styleElement);

            saveLocalFontToIndexedDB(fontFamilyName, fontDataUrl, file.name)
                .then(() => {
                    state.localFonts.push({ fontName: fontFamilyName, fontDataUrl, fileName: file.name, dateAdded: new Date().toISOString() });
                    state.importedFontFamilies.push(fontFamilyName);
                    saveFontsToStorage();
                    showNotification(`æœ¬åœ°å­—ä½“ \"${fontFamilyName}\" å·²æˆåŠŸå¯¼å…¥å¹¶æŒä¹…åŒ–ã€‚`);
                    fileInput.value = '';
                    nameInput.value = '';
                    renderCombinedMainView();
                })
                .catch(error => {
                    showNotification(`å­—ä½“å·²åŠ è½½ï¼Œä½†æŒä¹…åŒ–å­˜å‚¨å¤±è´¥: ${error.message || error}`, 'error');
                });
        } catch (err) {
            showNotification('å¤„ç†æœ¬åœ°å­—ä½“æ–‡ä»¶æ—¶å‘ç”Ÿé”™è¯¯: ' + (err.message || err), 'error');
        }
    };
    reader.onerror = (err) => showNotification('è¯»å–æœ¬åœ°å­—ä½“æ–‡ä»¶æ—¶å‘ç”Ÿé”™è¯¯: ' + (err.message || err), 'error');
    reader.readAsDataURL(file);
}

