// ========= é¢œè‰²å¤„ç†è¾…åŠ©å‡½æ•° =========
function hexToRgb(hex) {
    if (!hex) return null;
    const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
    return result ? {
        r: parseInt(result[1], 16),
        g: parseInt(result[2], 16),
        b: parseInt(result[3], 16)
    } : null;
}

function parseColor(colorString, fallback = 'rgba(0,0,0,1)') {
    if (colorString && typeof colorString === 'string') {
        let match = colorString.match(/^rgba?\((\d+),\s*(\d+),\s*(\d+)(?:,\s*([\d.]+))?\)$/);
        if (match) {
            return {
                r: parseInt(match[1]),
                g: parseInt(match[2]),
                b: parseInt(match[3]),
                a: match[4] !== undefined ? parseFloat(match[4]) : 1,
            };
        }
        const rgb = hexToRgb(colorString);
        if (rgb) {
            return { ...rgb, a: 1 };
        }
    }

    if (fallback && typeof fallback === 'string') {
        let fallbackMatch = fallback.match(/^rgba?\((\d+),\s*(\d+),\s*(\d+)(?:,\s*([\d.]+))?\)$/);
        if (fallbackMatch) {
             return {
                r: parseInt(fallbackMatch[1]),
                g: parseInt(fallbackMatch[2]),
                b: parseInt(fallbackMatch[3]),
                a: fallbackMatch[4] !== undefined ? parseFloat(fallbackMatch[4]) : 1,
            };
        }
        const fallbackRgb = hexToRgb(fallback);
        if (fallbackRgb) {
            return { ...fallbackRgb, a: 1 };
        }
    }

    console.warn(`Font Manager: æ— æ³•è§£æé¢œè‰² "${colorString}" æˆ–å¤‡ç”¨é¢œè‰² "${fallback}"ã€‚å°†ä½¿ç”¨ç»å¯¹é»˜è®¤å€¼ã€‚`);
    return { r: 0, g: 0, b: 0, a: 1 };
}


