function initializeIndexedDB() {
    return new Promise((resolve, reject) => {
        if (!window.indexedDB) {
            console.error('Font Manager: æ‚¨çš„æµè§ˆå™¨ä¸æ”¯æŒ IndexedDBï¼Œæœ¬åœ°å­—ä½“å°†æ— æ³•æŒä¹…åŒ–å­˜å‚¨ã€‚');
            reject('æµè§ˆå™¨ä¸æ”¯æŒ IndexedDB');
            return;
        }

        const request = indexedDB.open(INDEXED_DB_NAME, INDEXED_DB_VERSION);

        request.onerror = function (event) {
            console.error('Font Manager: æ‰“å¼€ IndexedDB å¤±è´¥:', event.target.error);
            reject(event.target.error);
        };

        request.onupgradeneeded = function (event) {
            const db = event.target.result;

            if (!db.objectStoreNames.contains(FONT_STORE_NAME)) {
                const store = db.createObjectStore(FONT_STORE_NAME, { keyPath: 'fontName' });
                store.createIndex('fontName', 'fontName', { unique: true });
                store.createIndex('dateAdded', 'dateAdded', { unique: false });
                console.log('Font Manager: åˆ›å»ºäº†å­—ä½“æ•°æ®å­˜å‚¨ç©ºé—´');
            }
        };

        request.onsuccess = function (event) {
            const db = event.target.result;
            console.log('Font Manager: IndexedDB æˆåŠŸæ‰“å¼€');
            resolve(db);
        };
    });
}

function saveLocalFontToIndexedDB(fontName, fontDataUrl, fileName) {
    return new Promise((resolve, reject) => {
        initializeIndexedDB().then(db => {
            const transaction = db.transaction([FONT_STORE_NAME], 'readwrite');
            const store = transaction.objectStore(FONT_STORE_NAME);

            const fontData = {
                fontName: fontName,
                fontDataUrl: fontDataUrl,
                fileName: fileName,
                dateAdded: new Date().toISOString()
            };

            const request = store.put(fontData);

            request.onsuccess = function () {
                console.log(`Font Manager: æˆåŠŸä¿å­˜æœ¬åœ°å­—ä½“ \"${fontName}\" åˆ° IndexedDB`);
                resolve();
            };

            request.onerror = function (event) {
                console.error(`Font Manager: ä¿å­˜æœ¬åœ°å­—ä½“ \"${fontName}\" åˆ° IndexedDB å¤±è´¥:`, event.target.error);
                reject(event.target.error);
            };

            transaction.oncomplete = function () {
                db.close();
            };
        }).catch(error => {
            reject(error);
        });
    });
}

function loadLocalFontsFromIndexedDB() {
    return new Promise((resolve, reject) => {
        initializeIndexedDB().then(db => {
            const transaction = db.transaction([FONT_STORE_NAME], 'readonly');
            const store = transaction.objectStore(FONT_STORE_NAME);
            const request = store.getAll();

            request.onsuccess = function () {
                const fonts = request.result;
                console.log(`Font Manager: ä» IndexedDB åŠ è½½äº† ${fonts.length} ä¸ªæœ¬åœ°å­—ä½“`);
                state.localFonts = fonts;
                resolve(fonts);
            };

            request.onerror = function (event) {
                console.error('Font Manager: ä» IndexedDB åŠ è½½å­—ä½“å¤±è´¥:', event.target.error);
                reject(event.target.error);
            };

            transaction.oncomplete = function () {
                db.close();
            };
        }).catch(error => {
            reject(error);
        });
    });
}

function applyLocalFontsToPage() {
    if (!state.localFonts || state.localFonts.length === 0) {
        console.log('Font Manager: æ²¡æœ‰æœ¬åœ°å­—ä½“éœ€è¦åº”ç”¨');
        return;
    }

    console.log(`Font Manager: å¼€å§‹åº”ç”¨ ${state.localFonts.length} ä¸ªæœ¬åœ°å­—ä½“åˆ°é¡µé¢`);

    state.localFonts.forEach((fontData, index) => {
        if (!fontData.fontName || !fontData.fontDataUrl) {
            console.error(`Font Manager: æœ¬åœ°å­—ä½“æ•°æ®ä¸å®Œæ•´ï¼Œè·³è¿‡åº”ç”¨:`, fontData);
            return;
        }

        const fontStyleId = `font-manager-local-font-${fontData.fontName.replace(/\s+/g, '-')}`;
        if (docContext.getElementById(fontStyleId)) {
            console.log(`Font Manager: æœ¬åœ°å­—ä½“ \"${fontData.fontName}\" å·²åº”ç”¨ï¼Œè·³è¿‡`);
