// ========= Safe Storage Wrapper =========
const safeLocalStorage = {
    getItem: (key) => {
        try {
            return localStorage.getItem(key);
        } catch (e) {
            console.error(`Font Manager: Error getting item "${key}" from localStorage.`, e);
            showNotification(`è¯»å–æœ¬åœ°å­˜å‚¨å¤±è´¥: ${e.message}`, 'error');
            return null;
        }
    },
    setItem: (key, value) => {
        try {
            localStorage.setItem(key, value);
        } catch (e) {
            console.error(`Font Manager: Error setting item "${key}" to localStorage.`, e);
            if (e.name === 'QuotaExceededError') {
                showNotification('æœ¬åœ°å­˜å‚¨ç©ºé—´å·²æ»¡ï¼Œæ— æ³•ä¿å­˜è®¾ç½®ã€‚', 'error');
            } else {
                showNotification(`ä¿å­˜åˆ°æœ¬åœ°å­˜å‚¨å¤±è´¥: ${e.message}`, 'error');
            }
        }
    },
    removeItem: (key) => {
        try {
            localStorage.removeItem(key);
        } catch (e) {
            console.error(`Font Manager: Error removing item "${key}" from localStorage.`, e);
            showNotification(`ä»æœ¬åœ°å­˜å‚¨åˆ é™¤å¤±è´¥: ${e.message}`, 'error');
        }
    }
};

// ========= LocalStorage Functions =========
function saveFontsToStorage() {
    safeLocalStorage.setItem(LOCAL_STORAGE_KEY_FONTS, JSON.stringify(state.importedFontFamilies));
}

function saveFontImportUrlsToStorage() {
    safeLocalStorage.setItem(LOCAL_STORAGE_KEY_FONT_IMPORT_URLS, JSON.stringify(state.fontImportUrls));
}

function loadFontsFromStorage() {
    try {
        const storedFonts = safeLocalStorage.getItem(LOCAL_STORAGE_KEY_FONTS);
        if (storedFonts) {
            const parsedData = JSON.parse(storedFonts);
            if (Array.isArray(parsedData)) {
                state.importedFontFamilies = parsedData;
            } else {
                console.warn('Font Manager: Corrupted font list in safeLocalStorage. Resetting.');
                state.importedFontFamilies = [];
            }
        }
    } catch (e) {
        console.error('Font Manager: Error loading fonts from safeLocalStorage:', e);
        state.importedFontFamilies = [];
    }
}

function loadFontImportUrlsFromStorage() {
    try {
        const storedUrls = safeLocalStorage.getItem(LOCAL_STORAGE_KEY_FONT_IMPORT_URLS);
        if (storedUrls) {
            const parsedData = JSON.parse(storedUrls);
            if (Array.isArray(parsedData)) {
                state.fontImportUrls = parsedData;
            } else {
                console.warn('Font Manager: Corrupted font import URLs in safeLocalStorage. Resetting.');
                state.fontImportUrls = [];
            }
        } else {
            state.fontImportUrls = [];
        }
    } catch (e) {
        console.error('Font Manager: Error loading font import URLs from safeLocalStorage:', e);
        state.fontImportUrls = [];
    }
}


// ========= Runtime Style Host (imports) =========
function getOrCreateRuntimeHostStyle() {
    let el = docContext.getElementById(RUNTIME_HOST_STYLE_ID);
    if (!el) {
        el = docContext.createElement('style');
        el.id = RUNTIME_HOST_STYLE_ID;
        docContext.head.appendChild(el);
    }
    return el;
}

function renderRuntimeImports() {
    try {
        const host = getOrCreateRuntimeHostStyle();
        const imports = (state.fontImportUrls || []).map(url => `@import url('${url}');`).join('\n');
        host.textContent = imports;
    } catch (e) {
        console.error('Font Manager: Error rendering runtime imports:', e);
    }
}

