    try {
        if (state.multilangEnabled) {
            showNotification('å½“å‰å¤„äºå¤šè¯­è¨€å­—ä½“æ¨¡å¼ï¼Œè¯·åœ¨å¤šè¯­è¨€è®¾ç½®ä¸­é…ç½®å­—ä½“ï¼Œæˆ–å…ˆå…³é—­å¤šè¯­è¨€æ¨¡å¼ã€‚');
