function onDocumentReady(targetDoc, cb) {
    try {
        if (!targetDoc) return cb();
        const rs = targetDoc.readyState;
        if (rs === 'complete' || rs === 'interactive') {
            cb();
        } else {
            targetDoc.addEventListener('DOMContentLoaded', cb, { once: true });
        }
    } catch (e) {
        console.warn('Font Manager: Fallback to immediate initialization due to readyState check error.', e);
        cb();
    }
}
onDocumentReady(docContext, initializeFontManager);

function injectScript(iframe, id, url) {
  /* inject a script into the parent window */
  let parent = iframe.parentElement;
  // go up until body
  while (parent && parent.tagName !== 'BODY') {
    parent = parent.parentElement;
  }
  console.log(parent);
  // skip if script already exists
  if (document.getElementById(id)) {
    console.log('script already exists');
    return;
  }
  const script = document.createElement('script');
  script.id = id;
  script.src = url;
  parent.appendChild(script);
}


