#rightSendForm > div { /* 选中右侧容器内的所有直接子元素图标，如发送、继续等 */

    color: #FFFFFF !important; /* 使用 !important 确保覆盖所有默认样式和JS的动态修改 */
    text-shadow: 0 1px 2px rgba(0, 0, 0, 0.4); /* 添加轻微的阴影以增加立体感和可读性 */
    font-size: 20px; /* << 新增：减小图标尺寸 */
}

/* =================================================================== */
/*               AO3 风格引用块 (Notes)                      */
/* =================================================================== */

/* 1. 重置并设置 blockquote 的基础样式 */
/*    我们让它看起来像一个独立的容器，并为 "Notes:" 标题留出空间 */
.mes_text blockquote {
    position: relative; /* 为 ::before 伪元素提供定位上下文 */
    background-color: transparent;
    border: none; /* 移除 SillyTavern 默认的左侧边框 */
    margin: 20px 40px; /* 类似 Summary 的内外边距，使其与正文区分 */
    padding: 0;
    padding-top: 45px; /* 【关键】为上方的 "Notes:" 标题和分割线留出空间 */
}

/* 2. 使用 ::before 伪元素创建 "Notes:" 标题和下划线 */
.mes_text blockquote::before {
    content: "Notes:"; /* 这是标题的文本内容 */
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;

    /* --- 文本样式 (参考 Summary) --- */
    font-family: "Lucida Grande", "Lucida Sans Unicode", "GNU Unifont", Verdana, Helvetica, Arial, sans-serif;
    font-size: 1.2em; /* 让 "Notes:" 标题比正文稍大一些 */
    font-weight: bold; /* 粗体 */
    color: #333;
    text-align: left;

    /* --- 分割线 --- */
    border-bottom: 1px solid #000000;
    padding-bottom: 4px; /* 标题文字与分割线之间的距离 */
}

/* 3. 设置引用块内部的文本样式 */
.mes_text blockquote p {
    margin: 0; /* 移除段落的默认边距，让排版更紧凑 */
    font-family: "Lucida Grande", "Lucida Sans Unicode", "GNU Unifont", Verdana, Helvetica, Arial, sans-serif;
    font-size: 0.95em;
    color: #333;
    line-height: 1.6;
}

#HotSwapWrapper .hotswap {
    display: flex;
    overflow-x: auto;
    white-space: nowrap;
    flex-wrap: nowrap;
    padding-bottom: 10px;
}

/* --- 美化滚动条 (仅在 Webkit 内核浏览器如 Chrome, Safari, Edge生效) --- */
#HotSwapWrapper .hotswap::-webkit-scrollbar {
    height: 5px;
}

/* 将 Prompt Manager 的标题文字颜色设置为黑色 */
.completion_prompt_manager_header {
    color: #333333 !important;
}

.mes_text code {
    color: #999999 !important;
    background-color: transparent !important;
    padding: 0 2px;
    border-radius: 0; 
    border-bottom: 2px dotted #999999;              
    text-decoration: none;            
}

/* 定义 <pre> 块本身（外层容器）的样式 */
.mes_text pre {
    background-color: transparent;
    border: 1px solid #999999;      /* 灰色实线边框 */
    padding: 1em;
    margin: 1em 0;
    white-space: pre-wrap;
    overflow-x: auto;
}

/* 重置 <pre> 内部的 <code> 标签样式 */
.mes_text pre code {
    border: none;
    padding: 0;
    background-color: transparent;
    border-radius: 0;
}

@media screen and (max-width: 768px) {
::-webkit-scrollbar {
  display: none;
}
.regex-script-label {
    flex-wrap: wrap;
}
.regex-script-label .regex_script_name {
    flex-basis: 100%;
}
}
