@import url("https://fontsapi.zeoseven.com/281/main/result.css");


:root{
    --mainFontFamily:"Xiaolai";
    --monoFontFamily:"Xiaolai";
    --black30a: transparent;
    --black70a: rgb(249 200 224);
    --black50a: #f8f8f8;
    --grey30a: rgb(255 255 255 / 30%);
    --white50a: rgb(220 128 175 / 78%);
    --golden: rgb(219 128 175 / 56%);
    --white30a: rgb(255 218 224 / 50%);
}

/* 隐藏滑动条 */
*{
  scrollbar-width: none; /* Firefox */
  -ms-overflow-style: none; /* IE/Edge */
}

/* WebKit浏览器 (Chrome/Safari) */
*::-webkit-scrollbar {
  display: none;
  width: 0;
  height: 0;
}

/* 强制覆盖所有禁用元素的样式 */
[disabled], 
:disabled {
    /* 移除灰色滤镜和背景 */
    filter: none !important;
    background-color: transparent !important;
    background-image: none !important;

    /* 恢复文字颜色 */
    color: var(--text-normal) !important; /* 使用主题变量 */
    opacity: 1 !important;

    /* 修复边框 */
    border-color: var(--border-color-primary) !important;

    /* 修复图标颜色 */
    svg, img {
        opacity: 1 !important;
        filter: none !important;
    }
}

/* 针对特定禁用按钮的强化覆盖 */
button[disabled] {
    background: linear-gradient(to bottom, #f0f0f0, #e0e0e0) !important;
    color: #666 !important;
    border: 1px solid #d0d0d0 !important;
}
/* 隐藏滑动条 */
*{
  scrollbar-width: none; /* Firefox */
  -ms-overflow-style: none; /* IE/Edge */
}

/* WebKit浏览器 (Chrome/Safari) */
*::-webkit-scrollbar {
  display: none;
  width: 0;
  height: 0;
}
/* 修复输入框禁用状态 */
input[disabled], 
textarea[disabled] {
    background: #f8f8f8 !important;
    -webkit-text-fill-color: #333 !important; /* 修复 Safari/Chrome 文字颜色 */
}

* {
    filter: none !important;
    box-sizing: border-box;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    text-shadow: 0px 0px calc(var(--shadowWidth) * 1px) var(--SmartThemeShadowColor);
}

#chat {
    padding: 0px 15px;
    display: flex;
    align-items: center !important;
    background: transparent;
}
#chat .mes {
    display: flex;
    flex-direction: column;
    margin: 80px 0px 20px;
    padding: 0px;
    background: transparent;
    position: relative;
}
#chat .mes::before {
    content: "";
