    position: absolute;
    width: 60px;
    height: 60px;
    background-image: url(https://i.postimg.cc/V6TtRdRF/9.png);
    background-size: contain;
    background-repeat: no-repeat;
    bottom: -13px;
    right: 5px;
    pointer-events: none;
    z-index: 1;
}
#chat .mes::after {
    content: "";
    display: block;
    position: absolute;
    width: 130px;
    height: 22px;
    background-image: url(https://i.postimg.cc/qq3brtMj/910.png);
    background-size: contain;
    background-repeat: no-repeat;
