/* 角色选择 */
.character_select.entity_block.flex-container.interactable.is_fav, #user_avatar_block .bogus_folder_select, #user_avatar_block .character_select, #user_avatar_block .avatar-container.selected {
    background: #fef0f3 !important;
    padding: 10px;
    position: relative;
    margin: 5px 0px;
}
.character_select.entity_block.flex-container.interactable.is_fav::before, #user_avatar_block .bogus_folder_select::before, #user_avatar_block .character_select::before, #user_avatar_block .avatar-container.selected::before {
    content: "";
    display: block;
    position: absolute;
    background: url(https://iili.io/Kvc8Ciu.png);
    background-repeat: no-repeat;
    pointer-events: none;
    z-index: 10;
    width: 50px;
    height: 50px;
    background-size: contain;
    bottom: -2px;
    right: 0px;
}
.world_set, .toggleEnabled, .fav_on {
    color: var(--golden) !important;
}
.red_button, .redWarningBG {
    background-color: transparent !important;
}
.character_select.is_fav .avatar, .group_select.is_fav .avatar, .group_member.is_fav .avatar, .avatar.is_fav {
    outline: none;
}
code {
    border-right: 1px solid #fff;
    background: #fff7e8;
    color: rgb(229 191 156);
    padding: 1px 7px;
}
.code-toggle-button {
    background-color: rgb(255 235 242 / 38%) !important;
    color: #dca5b5ba !important;
}
.code-toggle-button:hover {
    background-color:  rgb(255 235 242 / 70%) !important;
}
.mes_text blockquote, .mes_reasoning blockquote {
    background: #fff7e8;
    color: #e5bf9c;
    border-left: 1.5px solid #e5bf9c;
}
.mes_text pre code {
    overflow: visible;
    background: #ffd0e044;
    color: #d394a7;
    border-radius: 0px;
}
