custom_css:
/*
 * === 主题：Manchester ===
 * 作者：亲爱的千雪小姐  
  仅发布在旅程美化区 其余均为倒卖*/


@import url('https://fontsapi.zeoseven.com/7/main/result.css');

:not([class*='fa-']):not(.fas):not(.far):not(.fal):not(.fab):not(.fa),
#send_textarea,
.menu_button_icon.fa-repeat,
.menu_button_icon.fa-bolt {
  font-family: 'Zhuque Fangsong (technical preview)';
}

.menu_button_icon.fa-repeat::before,
.menu_button_icon.fa-bolt::before {
  font-family: 'Zhuque Fangsong (technical preview)';
  font-weight: 900;
}

/*
 * =========================================
 * === 夜间模式 - 总控制台 ===
 * =========================================
 * 在这里调整所有颜色和自定义内容
 */
:root {
  /* 
    --- 1. 夜间模式颜色变量 ---
  */
  --dark-bg: rgb(36, 36, 37);                     /* 主背景色 (深灰) */
  --dark-panel-bg: rgb(45, 45, 48);               /* 面板/聊天框背景色 (稍亮) */
  --dark-text: rgb(255, 255, 255);                /* 主要文字颜色 (灰白) */
  --dark-text-subtle: rgb(255, 255, 255);         /* 次要/提示文字颜色 (灰色) */
  --dark-header-text: rgb(255, 255, 255);         /* 标题文字颜色 (更亮) */
  --dark-border: rgb(80, 80, 85);                 /* 边框颜色 */
  --dark-primary-accent: rgb(207, 207, 207);      /* 主要强调色 (淡蓝色，用于按钮/链接) */
  --dark-primary-accent-hover: rgb(211, 211, 211);/* 强调色悬停 */
  --dark-shadow-color: rgba(0, 0, 0, 0.4);        /* 阴影颜色 */
  --dark-selection-bg: rgba(255, 255, 255, 0.1);  /* 选中消息的背景色 */
  --dark-code-bg: rgba(0, 0, 0, 0.3);             /* 代码块背景色 */
  --dark-code-text: rgb(255, 255, 255);           /* 代码块文字颜色 (淡青色) */
  --dark-input-bg: rgb(30, 30, 32);               /* 输入框背景色 */
  --dark-scrollbar-thumb: #dbdbdb;               /* 滚动条滑块颜色 */

  /* 
    ---自定义内容总控制台 ---
  */
  --custom-avatar-text: "Hugo Su in Manchester ➤";
  --label-id-display: "关注";
  --label-timer: "粉丝";
  --label-token-counter: "小时";
  --user-profile-background-image: url('https://files.catbox.moe/8jb8ub.webp');
  --reasoning-header-text: "今日无日程";
  --custom-placeholder-text: "曼彻斯特的海边，我好想你。";
}


/* --- 以下为样式规则，已全部适配夜间模式 --- */

/* 基础背景设置 */
body {
  background-color: var(--dark-bg) !important;
  color: var(--dark-text) !important;
}

/* 代码块样式 */
code {
  background-color: var(--dark-code-bg);
  color: var(--dark-code-text);
  padding: 4px 8px;
  font-size: 1em;
  border-radius: 2px;
}
#chat .mes pre {
    background-color: var(--dark-code-bg) !important; 
    border-radius: 2px;
    overflow-x: auto;
    margin: 0.5em 0;
}
#chat .mes pre code {
    color: var(--dark-code-text) !important; 
    background: none !important;
}
#form_sheld {
    white-space: nowrap;
    width: 100%;
    margin: 0px auto 0 auto;
    z-index: 30;
}
/* 选中消息的样式 */
.mes.selected {
    background-color: var(--dark-selection-bg) !important; 
}
.mes.selected .mes_text {
    /* 在夜间模式下，选中时文本区域颜色不需要再改变 */
    background-color: transparent !important;
    color: var(--dark-text) !important;
}  

body * {
  box-shadow: none !important;
  filter: none !important;
}

/* 头像区域布局 */
.mes .mesAvatarWrapper {
    display: flex;
    flex-wrap: wrap; 
    flex-direction: row;
    justify-content: center;
    align-items: center;
    margin-bottom: 15px;
    width: 100%;
    padding: 2px 20px 5px 10px;
}
.mes .mesAvatarWrapper .avatar {
    flex-basis: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    margin-bottom: 8px;
}
.mesAvatarWrapper .avatar {
  pointer-events: none !important;
}


/* 1. 使用 .avatar::before 来显示头像框 */
#chat .mes .avatar::before {
  content: '';
  position: absolute;
  top: 50%; /* 调整垂直位置，使其围绕头像居中 */
  left: 50%;
  transform: translate(-50%, -50%);
  width: 135px; /* 头像框尺寸 */
  height: 135px;
  background-image: url('https://files.catbox.moe/m15toa.png'); /* 您的头像框图片 */
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  z-index: 2;
  pointer-events: none;
}


/* 2. 重新启用 .avatar::after 来显示自定义文字，并确保它不被头像框遮挡 */
.mes .mesAvatarWrapper .avatar::after {
    content: var(--custom-avatar-text); 
    display: block; 
    text-align: center;
    font-size: 11px;
    color: DBDBDB;
    margin-top: 12px; /* 文字和上方头像的间距 */
    position: relative; /* 确保文字在头像框的堆叠层级之上 */
    z-index: 3;
}

/* 3. 确保头像本身也在头像框之下 */
.mes .avatar {
  position: relative; /* 为绝对定位的伪元素提供定位上下文 */
  z-index: 1;
}

/* 元数据（关注、粉丝等） */
.mes .mesAvatarWrapper .mesIDDisplay,
.mes .mesAvatarWrapper .mes_timer,
.mes .mesAvatarWrapper .tokenCounterDisplay {
    flex: 1;
    text-align: center;
    min-width: 60px;
    margin-top: 35px;
    font-size: 12px;
    font-weight: 600;
    color: var(--dark-header-text);
    white-space: nowrap;
}
.mes .mesAvatarWrapper .mesIDDisplay::after,
.mes .mesAvatarWrapper .mes_timer::after,
.mes .mesAvatarWrapper .tokenCounterDisplay::after {
    font-size: 11px;
    font-weight: normal;
    color: var(--dark-text);
    margin-left: 5px;
    vertical-align: middle;
}
.mes .mesAvatarWrapper .mesIDDisplay::after { content: var(--label-id-display); }
.mes .mesAvatarWrapper .mes_timer::after { content: var(--label-timer); }
.mes .mesAvatarWrapper .tokenCounterDisplay::after { content: var(--label-token-counter); }

.mes .mesAvatarWrapper .mesIDDisplay:empty,
.mes .mesAvatarWrapper .mes_timer:empty,
.mes .mesAvatarWrapper .tokenCounterDisplay:empty {
    display: none;
}
.mes {
    flex-direction: column;
}

/* 顶部背景图 */
.mesAvatarWrapper:not([is_user=""]) {
  position: relative;
  padding-top: 100px; 
  overflow: hidden; 
}
.mes:not([is_user=""])::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 230px;
  background-image: var(--user-profile-background-image);
  background-size: cover;
  background-position: center center;
  background-repeat: no-repeat;
  -webkit-mask-image: linear-gradient(to bottom, black 60%, transparent 100%);
  mask-image: linear-gradient(to bottom, black 60%, transparent 100%);
  z-index: 0; 
}
.mes:not([is_user="true"]) .mesAvatarWrapper,
.mes:not([is_user="true"]) .mes_block {
  position: relative;
  z-index: 1;
}

/* 思维链 */
.mes_reasoning_header {
  border-radius: 3px !important;
  background-color: rgba(255, 255, 255, 0.05) !important;
  margin-top: 5px;
  margin-left: 8px;
}
.mes_reasoning_details { margin-top: 0px; margin-left: -2px; }
.mes_reasoning_actions { margin-top: 0px; margin-left: 0px; }
.mes_reasoning {
  margin-top: 10px;
  margin-bottom: 5px;
  padding: 15px;
  background-color: rgba(255, 255, 255, 0.03);
  width: 100%;
  margin-left: auto;
  margin-right: auto;
}
.mes_reasoning_header_title::before { 
  content: var(--reasoning-header-text); 
  font-size: var(--mainFontSize);
  font-style: italic;
  color: var(--dark-text-subtle);
}
.mes_reasoning_header_title { font-size: 0; }
.mes_reasoning_arrow { display: none !important; }
.ch_name > .flex-container:first-child { margin-left: 8px; }
.mes_text {
  text-align: justify;
  padding: 2px 20px 45px 10px;
}

/* 图标替换（保留原样） */
#extensionsMenuButton::before, #options_button::before {
  content: ""; display: inline-block; width: 30px; height: 30px;
  opacity: 1 !important; background-image: url("https://files.catbox.moe/agk054.png");
  background-size: contain; background-repeat: no-repeat; background-position: center;
  vertical-align: middle;
}
#send_but {
  background-image: url('https://files.catbox.moe/r94lvu.png'); background-size: contain;
  background-repeat: no-repeat; background-position: center; width: 30px;
  height: 30px; opacity: 0.85 !important;
}
#send_but::before { content: none !important; }
#send_but:hover { opacity: 0.8; cursor: pointer; }
#mes_stop > i {
  color: transparent; background-image: url('https://files.catbox.moe/ch9bsy.png');
  background-size: contain; background-repeat: no-repeat; background-position: center;
  display: inline-block; width: 1.2em; height: 1.2em;
}
.swipe_left, .swipe_right {
  background-image: url('https://files.catbox.moe/9237dh.png'); background-size: contain;
  background-repeat: no-repeat; background-position: center; font-size: 0 !important;
  opacity: 1 !important; width: 30px !important; height: 30px !important;
  transition: transform 0.2s ease; background-color: transparent !important;
}
.swipe_left { transform: scaleX(1); }
.swipe_right { transform: scaleX(-1); }
.swipe_left:hover { transform: scale(1.2); }
.swipe_right:hover { transform: scaleX(-1) scale(1.2); }


.last_mes {
  margin-bottom: 10px !important;
}
select:focus-visible, input:focus-visible, textarea:focus-visible {
  outline: none !important;
}
#send_form:hover #qr--bar {
    height: 35px;
    transition: all 0.5s ease;
}
#qr--bar {
    height: 0.5px;
    background: none !important;
}
#send_textarea {
    border: none !important;
    outline: none !important;
    box-shadow: none !important;
}
#nonQRFormItems { position: relative; }
#send_textarea::placeholder { color: transparent !important; text-shadow: none !important; }
#nonQRFormItems:has(#send_textarea:placeholder-shown)::before {
    /* --- 使用变量 --- */
    content: var(--custom-placeholder-text);
    color: #9f9f9fb5;
    left: 95px;
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    font-style: oblique;
    font-size: 0.5em;
    pointer-events: none;
    z-index: 10;
    white-space: nowrap; 
    overflow: hidden;
    text-overflow: ellipsis;
    max-width: calc(100% - 30px);
}

/* 主布局与面板 */
#sheld { background: none !important; border: none; box-shadow: none; backdrop-filter: none; }
#form_sheld, #chat {
    background-color: var(--dark-panel-bg);
    box-shadow: 0 8px 25px var(--dark-shadow-color);
    border: 1px solid var(--dark-border);
}
#chat { padding: 0px; } /* 移除内边距，让背景色充满 */

/* 标题与排版 */
.standoutHeader, .inline-drawer-header, #character_popup_text, h3 {
    color: var(--dark-header-text);
    border-bottom: 1px solid var(--dark-border);
    padding-bottom: 8px;
    margin-bottom: 15px;
}
h1, h2, h3, h4, h5, b, strong { color: var(--dark-header-text); }
a { color: var(--dark-primary-accent); text-decoration: none; font-weight: bold; }
a:hover { color: var(--dark-primary-accent-hover); }
h4, .standoutHeader, .title_restorable, .inline-drawer-toggle.inline-drawer-header {
    background: rgba(255, 255, 255, 0.07) !important;
    filter: none !important; border: none !important; text-shadow: none !important;
}

/* Prompt Manager 特殊颜色 */
#completion_prompt_manager .completion_prompt_manager_header_advanced span,
#completion_prompt_manager .completion_prompt_manager_header div {
  filter: none !important;
  color: var(--dark-text-subtle) !important;
}
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name .prompt-manager-inspect-action {
  color: #777 !important;
}
#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt .prompt_manager_prompt_controls span {
  filter: none !important; opacity: 1 !important;
}

/* 表单与交互元素 */
.menu_button, .api_button, input[type="submit"], input[type="button"] {
    background-color: var(--dark-primary-accent) !important;
    border: none !important;
    color: var(--dark-bg) !important; /* 深色文字以保证对比度 */
    border-radius: 2px !important; 
    transition: all 0.2s ease;
    padding: 6px 8px !important;
    font-weight: bold;
    text-shadow: none !important;
}
.menu_button:hover, .api_button:hover, input[type="submit"]:hover, input[type="button"]:hover {
    background-color: var(--dark-primary-accent-hover) !important;
    transform: scale(1.05);
    box-shadow: 0 6px 15px var(--dark-shadow-color);
}
.text_pole, textarea, input[type="text"], input[type="search"], input[type="number"],
.select2-container--default .select2-selection--single {
    background-color: var(--dark-input-bg) !important;
    border: 1px solid var(--dark-border) !important;
    border-radius: 2px !important;
    color: var(--dark-text) !important;
    padding: 5px 4px;
}
textarea:focus, input:focus, .select2-container--default .select2-selection--single:focus {
    border-color: var(--dark-primary-accent) !important;
    box-shadow: 0 0 0 3px rgba(130, 170, 220, 0.3);
    outline: none;
}
select {
    background-color: var(--dark-panel-bg);
    border: 1px solid var(--dark-border);
    border-radius: 5px;
    color: var(--dark-text);
    padding: 10px;
}
.redWarningBG, .red_button {
    background: linear-gradient(145deg, #5e3535, #7d2c2c) !important;
    border: none !important; color: #ffcdd2 !important;
}
.redWarningBG:hover, .red_button:hover {
    background: linear-gradient(145deg, #e57373, #ef5350) !important;
    color: white !important;
}
.world_set, .toggleEnabled { color: #82aaff !important; }
.fav_on { color: #ffcb6b !important; }

/* 顶部导航栏 */
#top-bar {
    border-bottom: 1px solid var(--dark-border);
}
.drawer-icon.fa-fw::before { content: '' !important; font-size: 0 !important; }
.drawer-icon.fa-fw {
    width: 30px; height: 30px; background-size: contain;
    background-repeat: no-repeat; background-position: center;
    transition: transform 0.3s ease;
}
.drawer-icon.fa-fw:hover { transform: scale(1.1); }
#ai-config-button .drawer-icon, #sys-settings-button .drawer-icon,
#advanced-formatting-button .drawer-icon, #WI-SP-button .drawer-icon,
#user-settings-button .drawer-icon, #backgrounds-drawer-toggle .drawer-icon,
#logo_block .drawer-icon, #extensions-settings-button .drawer-icon,
#table_database_settings_drawer .drawer-icon, #persona-management-button .drawer-icon,
#rightNavHolder .drawer-icon {
  background-image: url('https://files.catbox.moe/7bpuer.png');
}
#top-settings-holder .drawer-icon { opacity: 0.85; }

/* 滚动条 */
::-webkit-scrollbar { width: 7px; }
::-webkit-scrollbar-thumb {
  border: none !important; box-shadow: none !important;
  background-color: var(--dark-scrollbar-thumb) !important;
  border-radius: 4px;
}

/* 滑块 */
input[type='range'] {
  -webkit-appearance: none; appearance: none;
  width: 100%; background: transparent; cursor: pointer;
}
input[type='range']::-webkit-slider-runnable-track {
  width: 100%; height: 6px;
  background: linear-gradient(to right, #444, #888);
  border-radius: 3px; border: 1px solid rgba(0, 0, 0, 0.2);
}
input[type='range']::-webkit-slider-thumb {
  -webkit-appearance: none; appearance: none; margin-top: -14px;
  height: 35px; width: 35px;
  background-image: url('https://files.catbox.moe/7bpuer.png');
  background-size: contain; background-repeat: no-repeat;
  background-position: center; background-color: transparent;
  border: none; border-radius: 0; box-shadow: none;
  transition: transform 0.2s ease;
}
input[type='range']:hover::-webkit-slider-thumb,
input[type='range']:active::-webkit-slider-thumb { transform: scale(1.2); }
input[type='range']::-moz-range-track {
  height: 6px; background: linear-gradient(to right, #444, #888);
  border-radius: 3px; border: 1px solid rgba(0, 0, 0, 0.2);
}
input[type='range']::-moz-range-thumb {
  height: 35px; width: 35px; background-image: url('https://files.catbox.moe/7bpuer.png');
  background-size: contain; background-repeat: no-repeat; background-position: center;
  background-color: transparent; border: none; border-radius: 0;
}

/* 复选框 */
input[type='checkbox'] {
  appearance: none; -webkit-appearance: none; -moz-appearance: none;
  width: 18px; height: 18px; background-color: transparent;
  border: 2px solid var(--dark-text-subtle); border-radius: 4px;
  cursor: pointer; position: relative; vertical-align: middle;
  margin-right: 8px; transition: background-color 0.2s, border-color 0.2s;
}
input[type='checkbox']:checked {
  background-color: var(--dark-primary-accent);
  border-color: var(--dark-primary-accent);
  background-image: url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24' fill='white'%3e%3cpath d='M9 16.17L4.83 12l-1.42 1.41L9 19 21 7l-1.41-1.41z'/%3e%3c/svg%3e");
  background-size: 80%; background-position: center; background-repeat: no-repeat;
}

/* Select2 (世界书) 下拉选择框 */
.select2-container--default .select2-selection__choice {
    background-color: var(--dark-primary-accent) !important;
    border: none !important; border-radius: 8px !important;
    color: var(--dark-bg) !important;
    padding: 5px 10px !important; font-weight: 500; margin-top: 4px !important;
}
.select2-container--default .select2-selection__choice__remove {
    color: rgba(255, 255, 255, 0.5) !important;
    font-size: 1.2em; margin-right: 5px !important; transition: color 0.2s ease;
}
.select2-container--default .select2-selection__choice__remove:hover {
    color: var(--dark-bg) !important; transform: scale(1.1);
}
#select2-world_info-results .select2-results__option--selected {
  background-color: rgba(255, 255, 255, 0.15) !important;
  color: var(--dark-text) !important;
  font-weight: 200 !important;
}
#select2-world_info-results .select2-results__option--highlighted {
  background-color: var(--dark-primary-accent) !important;
  color: var(--dark-bg) !important;
}
.select2-search--inline { display: none; }
.select2-selection--multiple:focus-within .select2-search--inline,
.select2-selection--multiple.select2-container--focus .select2-search--inline {
    display: inline-block; width: auto; height: auto;
}
.select2_choice_clickable_buttonstyle+span.select2-container .select2-selection__choice__display {
    cursor: pointer; transition: background-color 0.2s;
    color: var(--dark-text); background-color: rgba(255, 255, 255, 0.1);
    white-space: break-spaces; word-break: break-all;
}

/* 移动端优化：隐藏滚动条 */
@media (max-width: 768px) {
    ::-webkit-scrollbar { display: none; }
    * { scrollbar-width: none; -ms-overflow-style: none; }
}
