custom_css:
/*
 * === 主题：Manchester ===
 * 作者：亲爱的千雪小姐  
  仅发布在旅程美化区 其余均为倒卖*/


@import url('https://fontsapi.zeoseven.com/7/main/result.css');

:not([class*='fa-']):not(.fas):not(.far):not(.fal):not(.fab):not(.fa),
#send_textarea,
.menu_button_icon.fa-repeat,
.menu_button_icon.fa-bolt {
  font-family: 'Zhuque Fangsong (technical preview)';
}

.menu_button_icon.fa-repeat::before,
.menu_button_icon.fa-bolt::before {
  font-family: 'Zhuque Fangsong (technical preview)';
  font-weight: 900;
}


:root {
--text-color: 656565!important;
  /* 
    在这里修改所有自定义的文字和图片 
  */

  /* 1. 自定义头像下的文字内容 */
  --custom-avatar-text: "Hugo Su in Manchester ➤";

  /* 2. 自定义标签内容 (关注、粉丝、小时) */
  --label-id-display: "关注";
  --label-timer: "粉丝";
  --label-token-counter: "小时";

  /* 3. 用户消息顶部的背景图片URL */
  --user-profile-background-image: url('https://files.catbox.moe/nihzxg.JPG');

  /* 4. 思维链部分的文字 */
  --reasoning-header-text: "今日无日程";

  /* 5. 输入框的自定义占位文字 */
  --custom-placeholder-text: "曼彻斯特的海边，我好想你。";
}


/* 针对所有单行 <code> 标签 */
code {
  background-color: #a8a8a818;
  color: rgb(92, 92, 92);
  padding: 4px 8px;
  font-size: 1em;
  border-radius: 2;
}
#chat .mes pre {
    /* --- 背景与边框 --- */
    background-color: #a8a8a818 !important; 
    border-radius: 2px; /* 圆角 */
    overflow-x: auto;
    margin: 0.5em 0;
}
#chat .mes pre code {
    /* --- **核心：文字颜色** --- */
    color: rgb(92, 92, 92) !important; 
    background: none !important;
}
.mes.selected {
    background-color: rgb(228, 228, 228) /* 自行更改颜色 */!important; 
}
.mes.selected .mes_text {
    background-color: var(--chiikawa-cream) !important; /* 保持消息文本区域颜色 */
    color: var(--chiikawa-brown) !important; /* 保持文字颜色 */
}  

body * {
  box-shadow: none !important;
  filter: none !important;
}
/* 定义 .mesAvatarWrapper 布局 */
.mes .mesAvatarWrapper {
    display: flex;
    flex-wrap: wrap; 
    flex-direction: row;
    justify-content: center;
    align-items: center;
    margin-bottom: 15px;
    width: 100%;
    padding: 2px 20px 5px 10px;
}
/*让头像及其下方的自定义文字独占一行 */
.mes .mesAvatarWrapper .avatar {
    flex-basis: 100%; /* 独占一行，迫使其余元素换行 */
    display: flex;
    flex-direction: column;
    align-items: center;
    margin-bottom: 8px; /* 头像与下方自定义文字的间距 */
}
.mesAvatarWrapper .avatar {
  pointer-events: none !important;
}
/* 使用 ::after 为头像添加额外的自定义文字 */
/*
 * =========================================
 * === 修正：同时显示头像框和自定义文字 ===
 * =========================================
 */

/* 1. 使用 .avatar::before 来显示头像框 */
#chat .mes .avatar::before {
  content: '';
  position: absolute;
  top: 50%; /* 调整垂直位置，使其围绕头像居中 */
  left: 50%;
  transform: translate(-50%, -50%);
  width: 135px; /* 头像框尺寸 */
  height: 135px;
  background-image: url('https://files.catbox.moe/e6b2te.png'); /* 您的头像框图片 */
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  z-index: 2;
  pointer-events: none;
}


/* 2. 重新启用 .avatar::after 来显示自定义文字，并确保它不被头像框遮挡 */
.mes .mesAvatarWrapper .avatar::after {
    content: var(--custom-avatar-text); 
    display: block; 
    text-align: center;
    font-size: 11px;
    color: rgba(30, 30, 30, 0.936);
    margin-top: 12px; /* 文字和上方头像的间距 */
    position: relative; /* 确保文字在头像框的堆叠层级之上 */
    z-index: 3;
}

/* 3. 确保头像本身也在头像框之下 */
.mes .avatar {
  position: relative; /* 为绝对定位的伪元素提供定位上下文 */
  z-index: 1;
}
.mes .mesAvatarWrapper .mesIDDisplay,
.mes .mesAvatarWrapper .mes_timer,
.mes .mesAvatarWrapper .tokenCounterDisplay {
    flex: 1; /* 平分空间 */
    text-align: center; /* 居中“数字+标签”组合 */
    min-width: 60px;
    margin-top: 35px; /* 元数据区和上方自定义文字的间距 */
    font-size: 12px; /* **数字**的字体大小，可以调大一些 */
    font-weight: 600; /* 让数字更突出 */
    color: rgb(12, 12, 12);
    white-space: nowrap;
}
/* 使用 ::after 将标签文字添加到数字后方 */
.mes .mesAvatarWrapper .mesIDDisplay::after,
.mes .mesAvatarWrapper .mes_timer::after,
.mes .mesAvatarWrapper .tokenCounterDisplay::after {
    font-size: 11px;     /* 标签的字体大小 */
    font-weight: normal; /* 标签使用常规字重 */
    color: rgb(30, 30, 30);
    margin-left: 5px;    /* 数字和标签之间的间距 */
    vertical-align: middle; /* 垂直对齐 */
}
/* --- 使用变量 --- */
.mes .mesAvatarWrapper .mesIDDisplay::after {
    content: var(--label-id-display);
}
.mes .mesAvatarWrapper .mes_timer::after {
    content: var(--label-timer);
}
.mes .mesAvatarWrapper .tokenCounterDisplay::after {
    content: var(--label-token-counter); 
}
.mes .mesAvatarWrapper .mesIDDisplay:empty,
.mes .mesAvatarWrapper .mes_timer:empty,
.mes .mesAvatarWrapper .tokenCounterDisplay:empty {
    display: none;
}
.mes {
    flex-direction: column;
}

.mesAvatarWrapper:not([is_user=""]) {
  position: relative; /* 使得伪元素可以相对于它进行绝对定位 */
  padding-top: 100px; /* 向上推开内容，为顶部的装饰图片留出空间 */ 
  overflow: hidden; 
}
.mes:not([is_user=""])::before {
  content: ''; /* 伪元素必须有 content 属性才能显示 */
  position: absolute; /* 绝对定位，脱离文档流，覆盖在顶部 */
  top: 0;
  left: 0;
  width: 100%;
  height: 230px; /* 装饰层的高度，可以比上面的 padding-top 稍大，以实现渐变重叠 */


  /* 3. 使用变量设置背景图片 */
  background-image: var(--user-profile-background-image);
  background-size: cover; /* 或 'contain'，根据您的图片调整 */
  background-position: center center; /* 将图片居中显示 */
  background-repeat: no-repeat;

  /* 4. 创建从上到下的渐变蒙版，实现“融入”效果 */
  -webkit-mask-image: linear-gradient(to bottom, black 60%, transparent 100%);
  mask-image: linear-gradient(to bottom, black 60%, transparent 100%);

  /* 确保装饰在内容之下，但在气泡背景之上 */
  z-index: 0; 
}

/* 5. 确保实际内容（头像和文本）在装饰层之上 */
.mes:not([is_user="true"]) .mesAvatarWrapper,
.mes:not([is_user="true"]) .mes_block {
  position: relative;
  z-index: 1;
}

/* 思维链 */
.mes_reasoning_header {
  border-radius: 3px !important;
}
.mes_reasoning_details { margin-top: 0px; margin-left: -2px; }
.mes_reasoning_header { margin-top: 5px; margin-left: 8px; background-color:#d0d0d045; }
.mes_reasoning_actions { margin-top: 0px; margin-left: 0px; }
.mes_reasoning {
  margin-top: 10%px;
  margin-bottom: 5px;
  padding: 15px;
  background-color: rgba(245, 245, 245, 0.194);
  width: 100%;
  margin-left: auto;
  margin-right: auto;
}
/* --- 使用变量 --- */
.mes_reasoning_header_title::before { content: var(--reasoning-header-text); font-size: var(--mainFontSize);font-style: italic;}
.mes_reasoning_header_title { font-size: 0; }
.mes_reasoning_arrow {
    display: none !important;
}
.ch_name > .flex-container:first-child {
  margin-left: 8px; 
}
.mes_text {
  text-align: justify;
  padding: 2px 20px 45px 10px;
}

/* --- 单独替换 #extensionsMenuButton 的图标 --- */
#extensionsMenuButton::before {
  content: "";
  display: inline-block;
  width: 30px;
  height: 30px;
  opacity: 1 !important;
  background-image: url("https://files.catbox.moe/agk054.png");
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  vertical-align: middle;
}
/* --- 单独替换 #options_button 的图标 --- */
#options_button::before {
  content: "";
  display: inline-block;
  width: 30px;
  height: 30px;
  opacity: 1 !important;
  background-image: url("https://files.catbox.moe/agk054.png");
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
}
/* --- 替换发送按钮为图片 (并强制不透明) --- */
#send_but {
  background-image: url('https://files.catbox.moe/r94lvu.png');
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  width: 30px;
  height: 30px;
  opacity: 0.85 !important;
}
#send_but::before {
  content: none !important;
}
#send_but:hover {
  opacity: 0.8; 
  cursor: pointer;
}

#mes_stop > i {
  /* 1. 通过设置颜色为透明来隐藏原来的Font Awesome图标 */
  color: transparent;

  background-image: url('https://files.catbox.moe/ch9bsy.png');

  background-size: contain;     /* 确保图片完整地显示在元素区域内 */
  background-repeat: no-repeat; /* 防止图片平铺重复 */
  background-position: center;  /* 将图片在元素内居中 */

  /* 4. (可选) 确保图标有合适的显示尺寸 */
  display: inline-block; /* 让 <i> 元素可以设置宽高 */
  width: 1.2em;          /* 设置宽度，em单位会随字体大小缩放 */
  height: 1.2em;         /* 设置高度 */
}


/* --- 
    美化 .swipe_left 和 .swipe_right (滑动箭头)
    替换为自定义图片
--- */

/* 1. **通用样式**：为左右两个箭头设置共同的样式 */
.swipe_left,
.swipe_right {
    /* --- **核心：替换为图片** --- */
    background-image: url('https://files.catbox.moe/9237dh.png');
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    font-size: 0 !important;
    opacity: 1 !important;

    /* --- 调整尺寸 --- */
    width: 35px !important;  
    height: 35px !important;
    transition: transform 0.2s ease;
    background-color: transparent !important;
}
.swipe_left {
    transform: scaleX(1); 
}
.swipe_right {
    transform: scaleX(-1);
}

.swipe_left:hover,
.swipe_right:hover {
    transform: scale(1.2);
}
.swipe_right:hover {
    transform: scaleX(-1) scale(1.2);
}

.last_mes {
