#advanced-formatting-button .drawer-icon { background-image: url('https://files.catbox.moe/7bpuer.png'); }
#WI-SP-button .drawer-icon { background-image: url('https://files.catbox.moe/7bpuer.png'); }
#user-settings-button .drawer-icon { background-image: url('https://files.catbox.moe/7bpuer.png'); }
#backgrounds-drawer-toggle .drawer-icon { background-image: url('https://files.catbox.moe/7bpuer.png'); }
#logo_block .drawer-icon { background-image: url('https://files.catbox.moe/7bpuer.png'); }
#extensions-settings-button .drawer-icon { background-image: url('https://files.catbox.moe/7bpuer.png'); }
