custom_css:
@font-face {
    font-family: 'DroidSansChinese';
    src: url("https://files.catbox.moe/p7k8dd.ttf") format('truetype'); /* 修改为新的字体链接 */
    font-weight: normal; /* 设置字体粗细为400 */
    font-style: normal;
}

:root {
    --mainFontFamily: 'DroidSansChinese';
    --black30a:transparent;
    --black70a: rgb(189 223 255 / 50%);
    --black50a: #f8f8f8;
    --grey30a: rgb(255 255 255 / 30%);
    --white50a: rgb(0 0 0 / 50%);
    --golden: rgb(140 162 216);
    --white30a: rgb(3 25 123 / 50%);
}

body, button, input, textarea, code, select {
  font-family: var(--mainFontFamily), "Microsoft YaHei", sans-serif !important;
}
/* 隐藏滑动条 */
*{
  scrollbar-width: none; /* Firefox */
  -ms-overflow-style: none; /* IE/Edge */
}

/* WebKit浏览器 (Chrome/Safari) */
*::-webkit-scrollbar {
  display: none;
  width: 0;
  height: 0;
}
/* 强制覆盖所有禁用元素的样式 */
[disabled], 
:disabled {
    /* 移除灰色滤镜和背景 */
    filter: none !important;
    background-color: transparent !important;
    background-image: none !important;

    /* 恢复文字颜色 */
    color: var(--text-normal) !important; /* 使用主题变量 */
    opacity: 1 !important;

    /* 修复边框 */
    border-color: var(--border-color-primary) !important;

    /* 修复图标颜色 */
    svg, img {
        opacity: 1 !important;
        filter: none !important;
    }
}

/* 针对特定禁用按钮的强化覆盖 */
button[disabled] {
    background: linear-gradient(to bottom, #f0f0f0, #e0e0e0) !important;
    color: #666 !important;
    border: 1px solid #d0d0d0 !important;
}

/* 修复输入框禁用状态 */
input[disabled], 
textarea[disabled] {
    background: #f8f8f8 !important;
    -webkit-text-fill-color: #333 !important; /* 修复 Safari/Chrome 文字颜色 */
}

* {
    filter: none !important;
    box-sizing: border-box;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    text-shadow: 0px 0px calc(var(--shadowWidth) * 1px) var(--SmartThemeShadowColor);
}

