}

/* 复选框/单选框 */
input[type="checkbox"],
input[type="radio"] {
  border-color: rgba(245, 245, 220, 0.6) !important; /* 未选中状态米白色边框 */
  background-color: rgba(245, 245, 220, 0.9) !important; /* 未选中状态米白色填充 */
  -webkit-appearance: none; /* 移除默认样式 */
  -moz-appearance: none;
  appearance: none;
  width: 17px; /* 确保大小一致 */
  height: 17px;
  border-radius: 50%; /* 复选框圆角 */
}
input[type="radio"] {
  border-radius: 50%; /* 单选框保持圆形 */
}
input[type="checkbox"]:checked,
input[type="radio"]:checked {
  background-color: rgba(173, 216, 230, 0.4) !important; /* 选中状态浅蓝色填充 */
  border-color: rgba(245, 245, 220, 0.8) !important; /* 米白色边框 */
}

/* 复选框选中时的勾号 */
input[type="checkbox"]:checked::after {
  content: '\2714'; /* Unicode勾号 */
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  color: rgba(245, 245, 220, 0.9); /* 米白色勾号 */
  font-size: 5px;
  font-weight: bold;
}

/* 单选框选中时的圆点 */
input[type="radio"]:checked::after {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 10px;
  height: 10px;
  background: rgba(245, 245, 220, 0.9); /* 米白色圆点 */
  border-radius: 50%;
}

