custom_css:
/* 本样式仅免费发布于Discord旅程社区。请勿二传或商用！二改请标注谢谢。感谢Junezz老师的简约卡片的正则参考。 */

/* 字体 */
@import url("https://fontsapi.zeoseven.com/371/main/result.css");

:root {
  /* 字体名称 */
  --mainFontFamily: "BabelStone Han";
  --monoFontFamily: "BabelStone Han";
  --模糊强度: 1px;
  --气泡左右间距: 10px;
  --正文左右间距: 15px;
  --自定义文案: "❘ 也无风雨也无晴。";
  --思维链标题: "——行到水穷处，坐看云起时。";
  --图标大小: 15px;
  --crimson70a: transparent;
  --theme-gradient: linear-gradient(135deg, var(--SmartThemeQuoteColor) 30%, var(--SmartThemeUnderlineColor) 70%);
}

/* 输入栏间距：上，左右，下三个数值 */
#send_form, body.no-blur #send_form {
  padding: 6px 10px 10px;
}

body.bubblechat .mes {
  padding: 10px var(--正文左右间距);
}

/* chat + mes */
#chat {
  border: none;
  padding: 20px var(--气泡左右间距);
}

#chat .mes + .mes {
  margin-top: 50px;
}

.mes_block .ch_name {
  margin-bottom: 10px; /* 头像区域与正文 */
}

.last_mes .mesAvatarWrapper {
  padding-bottom: 0px;
}

/* 头像区域 */
.mes {
  position: relative;
  flex-direction: column;
}

body.no-blur .mes {
  backdrop-filter: blur(var(--模糊强度)) !important;
}

.mes .avatar {
  position: absolute;
  top: 20px;
  left: 50%;
  transform: translateX(-50%);
}

.mes .ch_name .alignItemsBaseline {
  position: absolute;
  top: 80px; /* 20+头像高度50 */
  gap: 7px; /* 间距7 */
  left: 50%;
  transform: translateX(-50%);
  flex-direction: column;
  align-items: center !important;
  white-space: nowrap;
}

/* 当头像隐藏时，将名字上移 */
body.hideChatAvatars .mes .ch_name .alignItemsBaseline {
  top: 32px !important;
}

/* 隐藏 */
.mes[is_system="true"] .ch_name .alignItemsBaseline {
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: center;
}

.mes[is_system="true"] .timestamp {
  flex-basis: 100%;
  text-align: center;
}

.mes[is_system="true"] .mes_ghost {
  align-self: center;
}

.mesAvatarWrapper {
  position: absolute;
  top: 0;
  left: var(--正文左右间距);
  right: var(--正文左右间距);
  height: 190px; /* 高度190 */
  display: flex;
  align-items: end;
}

/* 当头像隐藏时，同步压缩这个包裹层的高度 */
body.hideChatAvatars .mesAvatarWrapper {
  height: 130px !important;
}

.mes .mes_block {
  margin-top: 240px; /* 高度240 */
}

/* 当头像隐藏时，将正文上移 */
body.hideChatAvatars .mes .mes_block {
  margin-top: 180px !important;
}

.mes .mesIDDisplay { order: 1; }
.mes .tokenCounterDisplay { order: 2; }
.mes .mes_timer { order: 3; }

.mes .tokenCounterDisplay {
  margin-left: auto;
}

.mes .mes_timer {
  margin-left: 5px; /* 间距5 */
}

/* -- 矩形头像适配 -- */
body.big-avatars .mesAvatarWrapper {
    height: 230px !important;
}
body.big-avatars .mes .mes_block {
    margin-top: 280px !important;
}
body.big-avatars .mes .ch_name .alignItemsBaseline {
    top: 120px !important;
}

/*矩形隐藏头像*/
body.big-avatars.hideChatAvatars .mesAvatarWrapper {
    height: 130px !important;
}
body.big-avatars.hideChatAvatars .mes .mes_block {
    margin-top: 180px !important;
}
body.big-avatars.hideChatAvatars .mes .ch_name .alignItemsBaseline {
    top: 32px !important;
}

/* 字体样式 */
.mes .name_text, .mes .timestamp, .mes .mes_timer, .mes .tokenCounterDisplay, .mes .mesIDDisplay {
  color: var(--SmartThemeBodyColor) !important;
  opacity: 0.9;
  font-weight: normal;
}

.mes .name_text, .mes .timestamp {
  font-size: calc(var(--mainFontSize) * 0.9);
  letter-spacing: 3px;
}

.mes .mes_timer, .mes .tokenCounterDisplay {
  font-size: calc(var(--mainFontSize) * 0.9);
  margin-bottom: 5px;
}

.mes .mesIDDisplay {
  font-size: calc(var(--mainFontSize) * 1.5);
}

/* 样式 */
.mes .name_text::before {
  content: "作/";
}

.mes .timestamp::before {
  content: "-";
  margin-right: 8px;
}

.mes .timestamp::after {
  content: "-";
  margin-left: 8px;
}

.mes .tokenCounterDisplay::before {
  content: "字数";
  margin-right: 3px;
}

.mes .tokenCounterDisplay::after {
  content: "|";
  margin-left: 3px;
}

.mes .mes_timer::before {
  content: "更新于";
  margin-right: 3px;
}

.mes .mes_timer::after {
  content: "前";
  margin-left: 3px;
}

.mes .mesIDDisplay::before {
  content: "章";
  margin-right: 3px;
}

/* 自定义文案 */
.mes .mesAvatarWrapper::after {
  content: var(--自定义文案);
  position: absolute;
  bottom: -30px;
  left: 0;
  font-size: calc(var(--mainFontSize) * 1.1);
  white-space: nowrap;
  color: var(--SmartThemeBodyColor);
}

/* 按钮图标 */
.mes_buttons .fa-pencil::before, .mes_buttons .fa-ellipsis::before {
  font-size: calc(var(--mainFontSize) * 0.9);
  font-weight: normal;
  vertical-align: top;
}

.mes_buttons .fa-pencil::before {
  content: "摘抄";
}

.mes_buttons .fa-ellipsis::before {
  content: "展开";
}

.mes_buttons, .extraMesButton {
  align-items: start;
  padding: 0;
}

.mes_button, .extraMesButtons>div, .favorite-toggle-icon, .mes_edit_buttons .menu_button, .mes_reasoning_actions .edit_button {
  opacity: 0.7 !important;
  filter: none !important;
  background-color: transparent;
  padding: 0 3px;
  font-size: calc(var(--mainFontSize) * 0.9);
}

.mes_edit_buttons .menu_button, .mes_reasoning_actions .edit_button {
  border-color: var(--SmartThemeBodyColor);
}

.mes_edit_buttons {
  filter: none;
  margin-top: 20px;
}

/* --- MODIFIED: 将顶部和底部样式分离，实现独立控制 --- */

/* 顶部状态栏的样式 */
#top-bar, body.no-blur #top-bar {
  background-color: rgba(248, 248, 248, 0.85) !important;
  outline: none !important;
  border: none !important;
  box-shadow: none;
  border-radius: 0;
  backdrop-filter: blur(var(--模糊强度)) !important;
}

/* 底部输入框的样式 */
#send_form, body.no-blur #send_form {
  background-color: rgba(248, 248, 248, 0.75) !important;
  outline: none !important;
  border: none !important;
  box-shadow: none;
  border-radius: 0;
  backdrop-filter: blur(var(--模糊强度)) !important;
}

#top-settings-holder .drawer {
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 6px 10px;

  background-color: rgba(230, 230, 230, 0.4);
  transition: background-color 0.2s ease-in-out;
}

#top-settings-holder .drawer:has(.openDrawer) {
  background-color: rgba(220, 220, 220, 0.8);
}

#top-settings-holder .drawer-icon {
  font-size: var(--图标大小) !important;
  border-radius: 0;
  transition: opacity 0.2s ease-in-out;
  opacity: 0.85;
  color: var(--SmartThemeBodyColor);
  background: none;
}

#top-settings-holder .drawer-icon:hover,
#top-settings-holder .drawer-icon.active-icon {
  opacity: 1;
  background: var(--theme-gradient);
  background-clip: text;
  -webkit-background-clip: text;
  color: transparent;
}

  font-size: var(--图标大小) !important;
}

#rightSendForm>div, #leftSendForm>div, #rightSendForm > div > [class*="fa-"], #rightSendForm > div > i, #quickPersona {
  font-size: calc(var(--图标大小) + 3px) !important;
}

#rightSendForm>div:not(.mes_stop), #leftSendForm>div {
  opacity: 1;
}

#send_form .mes_stop {
  padding: 0px 10px;
  opacity: 1;
}

#send_textarea, #send_textarea:focus {
  clip-path: none !important;
  border: none;
  backdrop-filter: none !important;
  color: var(--SmartThemeBodyColor) !important;
}

#send_textarea::placeholder {
  color: color-mix(in srgb, var(--SmartThemeBodyColor), transparent 60%) !important;
  opacity: 1;
}

#send_textarea::selection {
  background-color: var(--SmartThemeUnderlineColor);
  color: var(--SmartThemeBodyColor);
}

/* 菜单 */
#left-nav-panel,
#completion_prompt_manager_popup,
#rm_api_block,
#AdvancedFormatting,
#WorldInfo,
#user-settings-block,
#Backgrounds,
#rm_extensions_block,
.drawer-content.openDrawer,
#right-nav-panel,
body.no-blur .popup, body.no-blur #bulk_tag_popup, body.no-blur #dialogue_popup,
#options, #extensionsMenu, .popup .popper-modal {
  border: none;
  padding: 10px;
  border-radius: 0px;
  box-shadow: none;
  backdrop-filter: blur(var(--模糊强度)) !important;
  background-color: rgba(255, 255, 255, 0.85) !important;
}

#form_sheld {
  margin: 0 auto !important;
}

#sheld {
  top: var(--bottomFormBlockSize);
  height: calc(100dvh - var(--bottomFormBlockSize));
  max-height: calc(100dvh - var(--bottomFormBlockSize));
}

/* 图标样式 */
.fa, .fa-brands, .fa-classic, .fa-regular, .fa-sharp, .fa-solid:not(.fav_on, .world_set, .fa-power-off, .fa-chevron-right, .fa-chevron-left), .fab, .far, .fas {
  color: rgba(50, 50, 50, 0.95) !important;
  font-size: var(--图标大小) !important;
  filter: none;
  opacity: 1;
}

/* 图标按钮样式 */
.menu_button {
  color: rgba(50, 50, 50, 0.95);
  filter: none;
  background-color: transparent;
  border: none;
}

/* --- MERGED: 输入框、选择框等表单元素样式 --- */
/* 当输入框内有内容时，边框变为实线 (性能优化版) */
#rm_api_block input[type="text"]:not(:placeholder-shown),
#rm_api_block textarea:not(:placeholder-shown),
#AdvancedFormatting input[type="text"]:not(:placeholder-shown),
#AdvancedFormatting textarea:not(:placeholder-shown),
#user-settings-block input[type="text"]:not(:placeholder-shown),
#user-settings-block textarea:not(:placeholder-shown),
#persona-management-button.drawer input[type="text"]:not(:placeholder-shown),
#persona-management-button.drawer textarea:not(:placeholder-shown),
#rm_ch_create_block .text_pole:not(:placeholder-shown),
#rm_ch_create_block textarea:not(:placeholder-shown),
#WorldInfo .text_pole:not(:placeholder-shown),
#WorldInfo textarea:not(:placeholder-shown),
.text_pole:not(:placeholder-shown),
.textarea_compact:not(:placeholder-shown) {
    border-style: solid !important;
    border-color: var(--SmartThemeBodyColor) !important;
}
/* 为文本输入框增加状态边框 (性能优化版) */
#rm_api_block input[type="text"],
#rm_api_block textarea,
#AdvancedFormatting input[type="text"],
#AdvancedFormatting textarea,
#user-settings-block input[type="text"],
#user-settings-block textarea,
#persona-management-button.drawer input[type="text"],
#persona-management-button.drawer textarea,
#rm_ch_create_block .text_pole,
#rm_ch_create_block textarea,
#WorldInfo .text_pole,
#WorldInfo textarea,
.text_pole,
.textarea_compact {
    border-style: dashed !important;
    border-color: color-mix(in srgb, var(--SmartThemeBodyColor), transparent 60%) !important;
    border-width: 1px !important;
    background-color: transparent !important;
    padding: 4px 8px !important;
    border-radius: 0px;
    color: var(--SmartThemeBodyColor);
}

/* 为所有下拉选择框增加背景色以区分 */
#rm_api_block select,
#AdvancedFormatting select,
#user-settings-block select,
#persona-management-button.drawer select,
#rm_ch_create_block select,
#WorldInfo select,
select,
.select2-container--default .select2-selection--single,
.select2-dropdown {
    background-color: color-mix(in srgb, var(--SmartThemeUnderlineColor), transparent 85%) !important;
    border: none !important;
    border-radius: 0px;
    color: var(--SmartThemeBodyColor);
}

select:focus-visible, input:focus-visible, textarea:not(#send_textarea):focus-visible {
  outline: none !important;
  border-left: 1px solid var(--SmartThemeBodyColor) !important;
  padding-left: 10px;
}

/* --- MERGED: 指示灯样式的复选框 --- */
/* 复选框容器 */
#rm_api_block .checkbox_label,
#AdvancedFormatting .checkbox_label,
#user-settings-block .checkbox_label,
#persona-management-button.drawer .checkbox_label,
#anchor_checkbox label,
#power-user-option-checkboxes label,
.checkbox_label {
    display: flex;
    align-items: center;
    border: none !important;
    background-color: transparent !important;
    padding: 6px 8px !important;
    position: relative;
    border-radius: 4px;
    transition: background-color 0.2s ease-in-out;
    margin: 5px 0px;
}
/* 悬停效果 */
#rm_api_block .checkbox_label:hover,
#AdvancedFormatting .checkbox_label:hover,
#user-settings-block .checkbox_label:hover,
#persona-management-button.drawer .checkbox_label:hover,
.checkbox_label:hover {
    background-color: rgba(0, 0, 0, 0.05);
}
/* 隐藏原始复选框 */
#rm_api_block .checkbox_label input[type="checkbox"],
#AdvancedFormatting .checkbox_label input[type="checkbox"],
#user-settings-block .checkbox_label input[type="checkbox"],
#persona-management-button.drawer .checkbox_label input[type="checkbox"],
.checkbox_label input[type="checkbox"] {
    position: absolute;
    opacity: 0;
    width: 0;
    height: 0;
}
/* 指示灯“关闭”状态 */
#rm_api_block .checkbox_label::before,
#AdvancedFormatting .checkbox_label::before,
#user-settings-block .checkbox_label::before,
#persona-management-button.drawer .checkbox_label::before,
.checkbox_label::before {
    content: '';
    width: 7px;
    height: 7px;
    border-radius: 50%;
    background-color: #AFAFB6;
    transition: all 0.2s ease-in-out;
    margin-right: 8px;
    flex-shrink: 0;
}
/* 指示灯“开启”状态 */
#rm_api_block .checkbox_label:has(input[type="checkbox"]:checked)::before,
#AdvancedFormatting .checkbox_label:has(input[type="checkbox"]:checked)::before,
#user-settings-block .checkbox_label:has(input[type="checkbox"]:checked)::before,
#persona-management-button.drawer .checkbox_label:has(input[type="checkbox"]:checked)::before,
.checkbox_label:has(input[type="checkbox"]:checked)::before {
    background-color: #34C759;
    box-shadow: none;
}


/* 标题 */
strong::before, b::before, h4:not(.margin0) span::before, #persona-management-button.drawer .standoutHeader::before {
  content: "﹥";
  margin-right: 5px;
}

#extensions_settings .inline-drawer-toggle.inline-drawer-header, #extensions_settings2 .inline-drawer-toggle.inline-drawer-header, #user-settings-block h4, .standoutHeader, .inline-drawer-header {
  border: none;
  background-image: none;
}

h3.margin0 span:not(.note-link-span)::before, h3.margin0.flex1::before, h3.margin0.flex2::before, #rm_button_selected_ch h2::before {
  content: "《";
}

h3.margin0 span:not(.note-link-span)::after, h3.margin0.flex1::after, h3.margin0.flex2::after, #rm_button_selected_ch h2::after {
  content: "》";
}

/* Spacing */
.text_pole, select, .range-block-range-and-counter, .range-block, .range-block-title, .WIEntryHeaderTitleMobile, h1, h2, h3 {
  margin: 3px !important;
}

.auto_continue_settings_block {
  margin: 5px 0px;
}

/* 滑动条 */
input[type="range"], input[type="range"]:disabled {
  filter: none;
  box-shadow: none !important;
  background-color: var(--SmartThemeUnderlineColor) !important;
  height: 1px !important;
  border-radius: 0px !important;
}

input[type="range"]::-webkit-slider-thumb {
  border: 1px solid var(--SmartThemeBodyColor);
  cursor: pointer;
  background-color: #fff;
  box-shadow: none;
  width: 10px;
  height: 10px;
}

/* 正文编辑 */
.mes .reasoning_edit_textarea, .mes .edit_textarea {
  background-color: transparent !important;
  border: none !important;
  margin: 0;
  padding: 0;
  outline: none !important;
  box-shadow: none !important;
}

.mes .edit_textarea {
  padding: var(--正文左右间距) !important;
  margin: 30px 0 !important;
}

/* 正文 */
.mes_block {
  padding: 0 3px;
}

.mes_text {
  padding: 0;
  text-align: justify;
  line-height: 1.6;
}

.mes_text em, code:not(pre code.hljs) {
  color: var(--SmartThemeQuoteColor) !important;
  background-color: transparent;
  padding: 0px;
  border: none;
  font-style: normal;
}

.mes_text em::before, code:not(pre code.hljs)::before {
  content: "﹤";
}

.mes_text em::after, code:not(pre code.hljs)::after {
  content: "﹥";
}

/* 思维链 */
.mes_reasoning_details {
  max-width: 90%;
  margin: 0 auto 20px;
  position: relative;
  border: 1px solid var(--SmartThemeUnderlineColor);
  padding: 15px;
  padding-bottom: 0;
}

/* 按钮不溢出 */
.mes_reasoning_summary {
  margin-right: 0 !important;
}

.mes_reasoning_actions {
  margin: 0;
  align-items: center;
}

/* 内容 */
.mes_reasoning {
  color: var(--SmartThemeBodyColor);
  border-left: none;
  padding: 0 15px 0 0;
  margin: 0;
  font-size: calc(var(--mainFontSize) * 0.9);

  max-height: 0;
  opacity: 0;
  overflow: hidden;
  border-top: 1px solid transparent;
  transition: max-height 0.4s cubic-bezier(0.4, 0, 0.2, 1),
              opacity 0.3s ease,
              margin-top 0.4s cubic-bezier(0.4, 0, 0.2, 1),
              border-color 0.1s linear 0.2s;
}

.mes_reasoning_details[open] .mes_reasoning {
  border-top: 1px solid var(--SmartThemeUnderlineColor);
  margin-top: 5px;
  max-height: 250px; /* 在此修改展开后的固定高度 */
  overflow-y: auto;
  opacity: 1;
}

.mes_reasoning::-webkit-scrollbar {
    width: 5px;
}
.mes_reasoning::-webkit-scrollbar-track {
    background: transparent;
}
.mes_reasoning::-webkit-scrollbar-thumb {
    background-color: var(--SmartThemeUnderlineColor);
    border-radius: 3px;
}

.mes_reasoning_details::before, .mes_text pre code::before, .mes_text blockquote::before {
  content: "❝";
  position: absolute;
  top: -10px;
  left: 10px;
  font-size: 1.5em;
  line-height: 1;
  color: var(--SmartThemeUnderlineColor);
}

.mes_reasoning_details::after, .mes_text pre code::after, .mes_text blockquote::after {
  content: "❞";
  position: absolute;
  bottom: -16px;
  right: 10px;
  font-size: 1.5em;
  line-height: 1;
  color: var(--SmartThemeUnderlineColor);
}

.mes_reasoning_header {
  margin: 0;
  padding: 0;
  padding-bottom: 15px;
  background: none;
}


.mes_reasoning_header_title {
  font-size: 0;
}

.mes .mes_reasoning_header_title::before {
  content: var(--思维链标题);
  font-size: var(--mainFontSize);
  color: var(--SmartThemeUnderlineColor);
  font-weight: bold;
  font-style: italic;
}

.mes_reasoning_details .mes_reasoning_arrow {
  display: none;
}

.mes_text pre code, .mes_text blockquote {
  position: relative;
  border: 1px solid var(--SmartThemeUnderlineColor);
  background-color: transparent;
  border-radius: 0;
  overflow: visible;
  padding: 15px;
  margin: 15px 0;
  line-height: 1.6;
}

/* 预设小字 */
.toggle-description {
  color: var(--SmartThemeUnderlineColor);
  opacity: 0.85;
}

/* 预设提示词标题 */
#completion_prompt_manager .completion_prompt_manager_header_advanced span,
#completion_prompt_manager .completion_prompt_manager_header div {
  filter: none;
  color: var(--SmartThemeBodyColor);
}

/* 预设条目 */
#completion_prompt_manager #completion_prompt_manager_list {
  gap: 5px;
  border: none !important;
  padding: 0 !important;
}

/* 启用预设条目 */
#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt:not(#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt.completion_prompt_manager_prompt_disabled) {
  border: 1px solid var(--SmartThemeUnderlineColor);
}

/* 未启用预设条目 */
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt.completion_prompt_manager_prompt_disabled,
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name, #completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name .prompt-manager-inspect-action {
  border: none;
  color: var(--SmartThemeUnderlineColor);
}

#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt .prompt_manager_prompt_controls span {
  filter: none !important;
  opacity: 1 !important;
}

/* --- MERGED: 世界书样式 (Outline 修正方案) --- */
.wi-card-entry {
  outline: 1px dashed color-mix(in srgb, var(--SmartThemeUnderlineColor), transparent 60%);
  outline-offset: -1px;
  border: none;

  border-radius: 0;
  margin: 10px 0;
  padding: 5px 8px;
  box-sizing: border-box;
}
/* 启用的世界书条目 */
.wi-card-entry:has(.killSwitch.fa-toggle-on) {
  outline: 1px solid var(--SmartThemeUnderlineColor);
  outline-offset: -1px;
}

/* 图片 */
.bg_example {
  border: none;
  box-shadow: none;
}

.extensions_info .extension_block {
  border: none;
}

/* QR */
.popup:has(#qr--modalEditor) .popup-content > #qr--modalEditor > #qr--main > .qr--modal-messageContainer > #qr--modal-messageHolder > #qr--modal-message {
  color: var(--SmartThemeBodyColor) !important;
}

/* 正则 */
.regex-script-label {
  background-color: transparent;
  border: 1px solid var(--SmartThemeUnderlineColor) !important;
  margin-bottom: 5px;
  border-radius: 0px !important;
}

.regex-script-label:has(.disable_regex:checked) {
  border: none !important;
}

@media (max-width: 600px) {
  .regex-script-label {
    flex-wrap: wrap;
    gap: 4px !important;
    padding-top: 8px !important;
    padding-bottom: 4px !important;
  }
  .regex-script-label > div:last-of-type {
    flex-basis: 100%;
    width: 100%;
  }
}

/* 人设 */
.buttons_block .menu_button {
  filter: none;
  opacity: 1;
  box-shadow: none;
}

.avatar-container.selected {
  border: 1px solid var(--SmartThemeUnderlineColor);
  border-radius: 0px;
}

.avatar img {
  box-shadow: none;
  border: none;
}

body.square-avatars .avatar, body.square-avatars .avatar img {
  border-radius: 0 !important;
}

/* 角色卡 */
.avatars_inline {
  gap: 5px;
  flex-wrap: nowrap;
  overflow: visible;
}

#HotSwapWrapper {
  overflow-x: auto;
}

.rm_tag_filter .tag {
  opacity: 1;
  filter: none;
  border: none;
}

.character_select.is_fav .ch_name, .group_select.is_fav .ch_name, .group_member.is_fav .ch_name {
  color: var(--SmartThemeBodyColor);
  text-decoration: dotted underline var(--SmartThemeBodyColor);
  overflow: visible;
}

.character_select.is_fav .avatar, .group_select.is_fav .avatar, .group_member.is_fav .avatar, .avatar.is_fav {
  outline: none !important;
}

/* 底栏菜单+QR */
.options-content, .list-group {
  border: none;
}

#extensionsMenu>.extension_container>div, #extensionsMenu>div:not(.extension_container), .options-content a, .list-group-item, #qr--bar {
  opacity: 1 !important;
}

#qr--bar > .qr--buttons .qr--button, #qr--popout > .qr--body > .qr--buttons .qr--button {
  border-radius: 0px !important;
  border: none !important;
  border-left: 1px solid color-mix(in srgb, var(--SmartThemeUnderlineColor), transparent 60%) !important;
  padding: 0 0 0 5px !important;
}

.qr--buttons .qr--button:hover {
  background-color: rgba(0, 0, 0, 0.08) !important;
}

/* QR显隐 */
#send_form:hover #qr--bar:has(.qr--button) {
    max-height: 200px !important;
    transition: all 0.5s ease !important;
}
#qr--bar {
    max-height: 0.1px !important;
    height: auto !important;
    transition: all 0.5s ease !important;
    overflow: hidden !important;
}

/* Swipe */
.swipe_left {
  left: 10px;
}

.swipeRightBlock, .swipe_left {
  margin-bottom: 10px;
}

.swipe_left, .swipe_right, .swipe_right fa-solid fa-chevron-right interactable, .swipes-counter {
  opacity: 1 !important;
  font-weight: normal;
  font-size: var(--mainFontSize);
}

.swipes-counter {
  font-size: calc(var(--mainFontSize) * 0.9) !important;
}

#chat .mes:last-of-type {
  padding-bottom: 50px !important;
}

/* Chat Top Bar */
#extensionTopBar, #extensionConnectionProfiles {
  border-radius: 0px !important;
  border: none !important;
  backdrop-filter: blur(var(--模糊强度)) !important;
}

/* 滚动条 */
::-webkit-scrollbar {
  width: 3px;
  background-color: transparent;
}

::-webkit-scrollbar-thumb {
  border: none !important;
  box-shadow: none !important;
  background-color: var(--SmartThemeUnderlineColor) !important;
}

#HotSwapWrapper::-webkit-scrollbar, #tableContainer>div::-webkit-scrollbar, .history-tabs::-webkit-scrollbar {
  display: none;
}

@media screen and (max-width: 700px) {
  ::-webkit-scrollbar {
    display: none;
  }
}

/* Font Opacity */
small, input.neo-range-slider[type="range"] {
  opacity: 1 !important;
  color: var(--SmartThemeQuoteColor) !important;
}

hr {
  opacity: 0.3;
  margin: 15px 0;
  background-color: var(--SmartThemeUnderlineColor);
}

.fillLeft .scrollableInner {
  padding: 0;
}

.mes_block {
  transform: none !important;
}

/* --- 世界书面板溢出修复方案 --- */
@media screen and (max-width: 768px) {
  #world_popup {
    width: 92vw !important;
    max-width: 92vw !important;
    box-sizing: border-box !important;
    margin: 0 !important;
  }
}

#WIEntryHeaderTitlesPC {
    padding-left: 10px !important;
    padding-right: 10px !important;
}

#world_popup .flex-container {
    flex-wrap: wrap !important;
}

.WIEntryTitleAndStatus {
    min-width: 0 !important;
}
.WIEntryTitleAndStatus > .flex-container.flex1 {
    min-width: 0 !important;
}
.world_entry_form textarea[name="comment"] {
    white-space: nowrap !important;
    overflow: hidden !important;
    text-overflow: ellipsis !important;
}

/* --- 简易三端 --- */
@media screen and (min-width: 1280px) {

    #top-settings-holder .drawer .drawer-content.openDrawer {
        position: absolute !important;
        top: calc(var(--topBarBlockSize) + 5px) !important;
        left: 50% !important;
        transform: translateX(-50%) !important;
        width: var(--sheldWidth) !important;
        max-width: 92vw !important;
        height: auto !important;
        max-height: calc(100dvh - var(--topBarBlockSize) - var(--bottomFormBlockSize) - 25px) !important;
        border-radius: 0 0 12px 12px !important;
        box-shadow: 0 5px 20px rgba(0, 0, 0, 0.1) !important;
        border: 1px solid rgba(199, 199, 204, 0.5) !important;
        border-top: none !important;
        backdrop-filter: blur(var(--blur_strength, 10px)) !important;
        background-color: var(--bot_mes_blur_tint_color) !important;
    }

    #top-settings-holder #left-nav-panel.openDrawer,
    #top-settings-holder #right-nav-panel.openDrawer {
        position: fixed !important;

        transform: none !important;
        top: calc(var(--topBarBlockSize) + 10px) !important;
        height: calc(100dvh - var(--topBarBlockSize) - var(--bottomFormBlockSize) - 25px) !important;
        width: calc((100vw - var(--sheldWidth)) / 2 - 20px) !important;
        max-width: none !important;

        /* 修正边框和圆角 */
        border-radius: 12px !important;
        border-top: 1px solid rgba(199, 199, 204, 0.5) !important;
    }

    /* 左侧边栏：预设菜单 */
    #top-settings-holder #left-nav-panel.openDrawer {
        left: 15px !important;
        right: auto !important;
    }

    /* 右侧边栏：角色卡菜单 */
    #top-settings-holder #right-nav-panel.openDrawer {
        right: 15px !important;
        left: auto !important;
    }
}
