                3px 3px 6px rgba(0,0,0,0.05);

    color: #5c4c38;
    font-size: 16px;
    line-height: 1.6;
    font-family: 'Helvetica Neue', sans-serif;

    transition: border-color 0.3s ease, 
                box-shadow 0.3s ease;

    &:focus {
        border-color: #d0c4b0;
        box-shadow: inset 1px 1px 4px rgba(0,0,0,0.08),
                    3px 3px 8px rgba(0,0,0,0.08),
                    0 0 0 2px rgba(200, 190, 175, 0.3);
    }

    &::placeholder {
        color: #b0a090;
        opacity: 0.8;
    }

    scrollbar-width: thin;
    scrollbar-color: #d0c4b0 #f0e8dc;

    &::-webkit-scrollbar {
        width: 1px;
        background: #f0e8dc;
    }

    &::-webkit-scrollbar-thumb {
        background: #d0c4b0;
        border-radius: 1px;
    }
}
