/* ========== é¡¶éƒ¨æ¸å˜é®ç½© ========== */
#chat {
  /* ä¿ç•™åŸæœ‰æ ·å¼ */
  max-height: calc(100vh - calc(var(--topBarBlockSize) + var(--bottomFormBlockSize)));
  overflow-x: hidden;
  padding-bottom: 0;
  overflow-y: scroll;
  display: flex;
  bottom: 10px;
  backdrop-filter: blur(var(--SmartThemeBlurStrength));
  background-color: var(--SmartThemeChatTintColor);
  -webkit-backdrop-filter: blur(var(--SmartThemeBlurStrength));
  text-shadow: 0px 0px calc(var(--shadowWidth) * 1px) var(--SmartThemeShadowColor);
  flex-direction: column;
  z-index: 30;
  flex-grow: 1;

 /* é¡¶éƒ¨æ¸å˜é®ç½© */
  --top-fade-height: 40px; /* é¡¶éƒ¨æ¸å˜åŒºåŸŸé«˜åº¦ */
  --top-fade-start: 0px; /* é¡¶éƒ¨å¼€å§‹æ¸å˜çš„ä½ç½®ï¼ˆ0=ä»é¡¶éƒ¨å¼€å§‹ï¼‰ */

  /* åº•éƒ¨æ¸å˜é®ç½© */
  --bottom-fade-start: 100px; /* åº•éƒ¨æ¸å˜å¼€å§‹ä½ç½® */
  --bottom-fade-height: 99px; /* åº•éƒ¨æ¸å˜åŒºåŸŸé«˜åº¦ */
  --bottom-fade-end: 0px; /* å®Œå…¨æ¶ˆå¤±ä½ç½® */

  /* ç»„åˆé®ç½©æ•ˆæœ */
  mask-image: 

    /* é¡¶éƒ¨é®ç½©ï¼šä¸Šè™šä¸‹å® */
    linear-gradient(
      to bottom, 
      rgba(0,0,0,0) var(--top-fade-start), 
      rgba(0,0,0,1) calc(var(--top-fade-start) + var(--top-fade-height))
    ),

    /* åº•éƒ¨é®ç½©ï¼šä¸Šå®ä¸‹è™š */
    linear-gradient(
      to bottom, 
      rgba(0,0,0,1) 0%, 
      rgba(0,0,0,1) calc(100% - var(--bottom-fade-start)), 
      rgba(0,0,0,0) calc(100% - var(--bottom-fade-end))
    );
  -webkit-mask-image: 
    linear-gradient(
      to bottom, 
      rgba(0,0,0,0) var(--top-fade-start), 
      rgba(0,0,0,1) calc(var(--top-fade-start) + var(--top-fade-height))
    ),
    linear-gradient(
      to bottom, 
      rgba(0,0,0,1) 0%, 
      rgba(0,0,0,1) calc(100% - var(--bottom-fade-start)), 
      rgba(0,0,0,0) calc(100% - var(--bottom-fade-end))
    );
  mask-size: 100% 100%;
  -webkit-mask-size: 100% 100%;
  mask-composite: intersect; /* ç»„åˆä¸¤ä¸ªé®ç½© */
  -webkit-mask-composite: source-in; /* Safariå…¼å®¹ */
}
